            var channelTextReg = $("> a span", this).text();


            //REPLACE ANY SECTION THAT CONTAINS & WITH AND
            //$(".sw-channel-dropdown > li > a > span", this).text(function () {
            //    return $(this).text().replace("&", "and"); 
            //});

            //IGNORE HOME AND CALENDAR CHANNELS
            if(!$(this).hasClass("channel-icon")) {

                // SET THE CHANNELS INDEX
                var i = ($("#sw-channel-list-container li.sw-channel-item.cs-home-icon").length) ? $(this).index() - 5 : $(this).index() - 4;

                if(jQuery.trim(megaMenuData[i].megaMenuToggle) == "true"){

                    var backgroundColor = "";
                    backgroundColor = jQuery.trim(megaMenuData[i].channelBgColor);
                    $(".sw-channel-dropdown", this).css("background", backgroundColor);
                    if($(this).hasClass("active")){
                    	$(this).css("background",backgroundColor);
                    }
                    /*$("> a", this).hover(function(){
                    	if($(this).parent().hasClass("hover")){
                            $(this).css("background",backgroundColor);
                        } else {
                        	$(this).css("background","none");
                        }
                    });*/
                    $(this).hover(function(){
                    	$(this).css("background",backgroundColor);
                    }, function(){
                        $(this).css("background","none");
                    });

                    var ddTextColor = jQuery.trim(megaMenuData[i].channelTextColor);
                    var ddTextHoverColor = jQuery.trim(megaMenuData[i].channelTextHoverColor);


                    // ADD MEGA CHANNEL CLASS
                    $(this).addClass('mega-channel');
                    $(this).addClass("Channel-" + index);

                    // ADD CHANNEL CATEGORIES
                    var categoryColumn = '
';
                    var textNums = ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen", "twenty"];
                    var channelCats = megaMenuData[i].channelCats;
                    for(var c in channelCats) {
                        if(channelCats[c].channelCatHeader != "") {
                            var catSections = channelCats[c].channelCatSections.split("|");

                            categoryColumn += '
';
                            categoryColumn += 	'
' + channelCats[c].channelCatHeader + '';
                            categoryColumn += 	'';
                                                        for(var s in catSections) {
                                                            $("> .sw-channel-dropdown li", this).each(function(){
                                                                if($("a span", this).text() == jQuery.trim(catSections[s])){
                                                                    //categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                                                    categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                                                    } else {
                                                                        categoryColumn += '>' + jQuery.trim(catSections[s]) + '';
                                                                    }
                                                                }
                                                            });

                                                        }
                            categoryColumn += 	'';
                            categoryColumn += '';
                        }    
                    }
                    categoryColumn +=	'';
                    // ADD STRUCTURE TO THE DOM
                    $(".sw-channel-dropdown", this).html('




');

                    // ADD CHANNEL NAME
                    var titleColumn = '
' + jQuery.trim(megaMenuData[i].channelTitle) + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(titleColumn);

                    // ADD CAPTION
                    var captionColumn = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(captionColumn);

                    // ADD IMAGE
                    var imgColumn = '';
                    var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                        imgColumn = '';
                    }
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(imgColumn);
                    $(".sw-channel-dropdown .mega-columns .mega-menu-bottom", this).append(categoryColumn);

                    $(".sw-channel-dropdown li.mega-columns h2.mega-channel-title", this).css("color", ddTextHoverColor);

                    $(".sw-channel-dropdown li.mega-columns .mega-menu-bottom .mega-channel-cat-section-list li a", this).hover(function() {
                        $(this).addClass("hover").css("color", ddTextHoverColor);
                      }, function() {
                      	if($(this).parent().parent().parent().parent().parent().parent().attr("data-value-mm-text-color") == "white") {
                        	$(this).removeClass("hover").css("color","#ffffff");
                        } else {
                        	$(this).removeClass("hover").css("color","#393C3D");
                        }
                      }
                    );


                }   

            }


            if($(".mega-channel-cat", this).length == 2) {
            	/*if($("#gb-page.dashboard").length){
                	$(".sw-channel-dropdown", this).css("width","510");
                } else {
                	$(".sw-channel-dropdown", this).css("width","560");
                }*/
                $(this).addClass("col-count-2");
            }
            if($(".mega-channel-cat", this).length == 3) {
            	/*if($("#gb-page.dashboard").length){
                	$(".sw-channel-dropdown", this).css("width","730");
                } else {
                	$(".sw-channel-dropdown", this).css("width","780");
                }*/
                 $(this).addClass("col-count-3");
            }
            if($(".mega-channel-cat", this).length == 4) {
            	/*if($("#gb-page.dashboard").length){
                	$(".sw-channel-dropdown", this).css("width","974");
                } else {
                    $(".sw-channel-dropdown", this).css("width","1024");
                }*/
                 $(this).addClass("col-count-4");
            }

            //ADJUST POSITION OF DROPDOWN IF IT OVERFLOWS THE SCREEN
            /*var position = $(this).position();
              var leftPos = position.left;
              var windowWidth = $(window).width();
              var dropdownWidth = $(".sw-channel-dropdown",this).width();
              var isOverflowing = leftPos + dropdownWidth;
              if($("#gb-page.dashboard").length){
              	var offset = (isOverflowing - windowWidth) + 40 + 35 + 50;
              } else {
              	var offset = (isOverflowing - windowWidth) + 40 + 35;
              }
              if(isOverflowing >= windowWidth){
                  $(".sw-channel-dropdown",this).css("left",-offset);
              }*/

        });

        $("#gb-channel-list .sw-channel-item.mega-channel").each(function() {
            $(".sw-channel-dropdown", this).css("width", "1024px");
            setDropdownPosition($(this));
        });
        $(window).resize(function(){
            if(csGetBreakPoint() == "desktop") {
                $("#gb-channel-list .sw-channel-item.mega-channel").each(function(){
                    setDropdownPosition($(this));
                });
            }
        });
        function setDropdownPosition(element) {
            var dashboardIsPresent = $("#dashboard-sidebar").length;
            var windowWidth = (dashboardIsPresent) ? $(window).width() - 50 : $(window).width();
            var position = $(element).offset();
            var leftPos = position.left;
            var dropdownWidth = $(".sw-channel-dropdown", element).outerWidth(true);
            var isOverflowing = leftPos + dropdownWidth;
            var offset = (dashboardIsPresent) ? (isOverflowing - windowWidth) - 50 : (isOverflowing - windowWidth);

            if(isOverflowing >= windowWidth){
                $(".sw-channel-dropdown", element).css({"width": ((windowWidth < 1024) ? windowWidth : "1024px"), "max-width": "1024px", "left": -offset});
            } else {
                $(".sw-channel-dropdown", element).css({"width": ((windowWidth < 1024) ? windowWidth : "1024px"), "left": "0px"});
            }

        }  

    }

