        $(".sw-mystart-button.user-options .cs-user-dropdown-list li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".sw-mystart-button.user-options").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".sw-mystart-button.user-options .cs-user-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".sw-mystart-button.user-options .cs-user-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".sw-mystart-button.user-options .cs-user-dropdown-list li:last-child > a").focus();
				break;

                // CONSUME TAB KEY
				case csKeyCodes.tab:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".sw-mystart-button.user-options").focus();
				break;

			}
		});

