    function articleSlider() {

    	$(".gt-768 .hp-content .hp.column.one .content-left.top .ui-widget.app.headlines, .gt-768 .hp-content .hp.column.one .content-left.bottom .ui-widget.app.headlines, .gt-768 .hp-content.bottom .ui-widget.app.headlines").csArticleSlider({
            "viewerNum": [3, 3, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "transitionSpeed": .5, // SECONDS
            "extend": function(config, element, ArticleSlider) {
                ArticleSlider.AnimateSlider = function(props) {
                    // FOR SCOPE
                    var _this = this;

                    // DEFINE SOME VARIABLES
                    var breakPoint = this.GetBreakPoint();
                    var articleDiff = 0;
                    var cloneNum = 0;

                    // CALCULATE THE NUMBER OF CLONED ARTICLES NEEDED FOR THE TRANSITION
                    articleDiff = this.articleNum - breakPoint.viewNum;
                    cloneNum = breakPoint.viewNum - articleDiff;
                    if(cloneNum < 0) cloneNum = 0;

                    // BACK
                    if(props.direction == "back") {
                        // UPDATE SLIDER POSITION PROPERTY FOR PREPENDING THE LAST ARTICLE
                        this.sliderPosition = this.sliderPosition - (this.articleWidth * breakPoint.viewNum);

                        // UPDATE SLIDER POSITION AND PREPEND THE LAST ARTICLE
                        $(this.slider, element).css("left", this.sliderPosition);

                        // SET A VARIABLE TO THE NUMBER TO ARTICLES THAT WE CAN MOVE WITHOUT HAVING TO CLONE ANYTHING
                        var loopNum = (this.articleNum - breakPoint.viewNum > breakPoint.viewNum) ? breakPoint.viewNum : this.articleNum - breakPoint.viewNum;

                        // MOVE THE ARTICLES TO THE BEGINNING OF THE SLIDE PANEL ONE BY ONE SO THAT THEY ARE IN THE CORRECT ORDER
                        for(var l = 1; l <= loopNum; l++){
                            $(this.slider, element).prepend($(this.article + ":last-child", element));
                        }

                        // IF WE HAVE A CLONE NUMBER THEN WE NEED TO ITERATE AND CREATE CLONED ARTICLES FOR THE TRANSITION
                        if(cloneNum > 0) {
                            // CLONE AND MOVE THE ARTICLES TO THE BEGINNING OF THE SLIDE PANEL ONE BY ONE SO THAT THEY ARE IN THE CORRECT ORDER
                            for(var c = 1; c <= cloneNum; c++) {
                                $(this.slider, element).prepend($(this.article + ":nth-child(" + this.articleNum + ")", element).clone().addClass("article-clone"));
                            }
                        }

                        // UPDATE SLIDER POSITION PROPERTY WITH NEXT POSITION VALUE FOR ANIMATING
                        this.sliderPosition = this.sliderPosition + (this.articleWidth * breakPoint.viewNum);

                        // ANIMATE THE SLIDER
                        $(this.slider, element).animate({
                            "left": this.sliderPosition
                        }, config.transitionSpeed * 1000, function() {
                            // REMOVED THE CLONED ARTICLES
                            $(_this.article + ".article-clone", element).remove();

                            // IF WE HAVE CLONED ARTICLES
                            if(cloneNum > 0) {
                                // MOVE THE ARTICLES TO THE BEGINNING OF THE SLIDE PANEL ONE BY ONE SO THAT THEY ARE IN THE CORRECT ORDER
                                for(var c = 1; c <= cloneNum; c++){
                                    $(_this.slider, element).prepend($(_this.article + ":last-child", element));
                                }
                            }

                            // UPDATE ATTRIBUTES
                            _this.UpdateAttributes({ "updateNotification": true });

                            // RESET sliderIsAnimating PROPERTY
                            _this.sliderIsAnimating = false;
                        });
                    }

                    // NEXT
                    else if(props.direction == "next") {
                        // UPDATE SLIDER POSITION PROPERTY WITH NEXT POSITION VALUE FOR ANIMATING
                        this.sliderPosition = this.sliderPosition - (this.articleWidth * breakPoint.viewNum);

                        // IF WE HAVE A CLONE NUMBER THEN WE NEED TO ITERATE AND CREATE CLONED ARTICLES FOR THE TRANSITION
                        if(cloneNum > 0) {
                            // CLONE AND MOVE THE ARTICLES TO THE END OF THE SLIDE PANEL
                            for(var c = 1; c <= cloneNum; c++) {
                                $(this.slider, element).append($(this.article + ":nth-child(" + c + ")", element).clone().addClass("article-clone"));
                            }
                        }

                        // ANIMATE THE SLIDER
                        $(this.slider, element).animate({
                            "left": this.sliderPosition
                        }, config.transitionSpeed * 1000, function() {
                            // UPDATE SLIDER POSITION PROPERTY FOR PREPENDING THE FIRST ARTICLE
                            _this.sliderPosition = _this.sliderPosition + (_this.articleWidth * breakPoint.viewNum);

                            // REMOVE THE CLONED ARTICLES
                            $(_this.article + ".article-clone", element).remove();

                            // UPDATE SLIDER POSITION AND APPEND THE SET OF ARTICLES
                            $(_this.slider, element).css("left", _this.sliderPosition).append($(_this.article + ":nth-child(-n+" + breakPoint.viewNum + ")", element));

                            // UPDATE ATTRIBUTES
                            _this.UpdateAttributes({ "updateNotification": true });

                            // RESET sliderIsAnimating PROPERTY
                            _this.sliderIsAnimating = false;
                        });
                    }
                }
            }

        });
        $(".gt-768 .hp-content .hp.column.one .content-left-top .ui-widget.app.upcomingevents,.gt-768 .hp-content .hp.column.one .content-left-bottom .ui-widget.app.upcomingevents, .gt-768 .hp-content.bottom .ui-widget.app.upcomingevents").csArticleSlider({
            "viewerNum": [5, 3, 2, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
            "transitionSpeed": .5, // SECONDS
            "extend": function(config, element, ArticleSlider) {
                ArticleSlider.AnimateSlider = function(props) {
