                var recordNums = $(".ui-widget-detail .ui-articles > li",this).length;
                if($(this).hasClass("upcomingevents")){
                    var slideDays = "5";    
                } else {
                    var slideDays = "3"; 
                }
                var columnNums = Math.ceil(recordNums/slideDays); 


                listItem = 'li',
                listClass = 'ui-articles';
                var items_per_col = new Array();
                var items = $(this).find(listItem);
                var min_items_per_col = Math.ceil(items.length / columnNums);
                var difference = items.length - (min_items_per_col * columnNums);
                for (var i = 0; i < columnNums; i++) {
                    if (i < difference) {
                        items_per_col[i] = min_items_per_col + 1;
                        //items_per_col[i] = slideDays;
                    } else {
                        items_per_col[i] = min_items_per_col;
                    }
                }
                for (var i = 0; i < columnNums; i++) {
                    $(".cs-article-viewer .ui-articles",this).append($('
'));
                    for (var j = 0; j < items_per_col[i]; j++) {
                        var pointer = 0;
                        for (var k = 0; k < i; k++) {
                            pointer += items_per_col[k];
                        }
                        $(this).find('.cs-ui-articles').last().append(items[j + pointer]);
                    }
                }

        });

