    // SETTINGS OBJECT WITH USER SAVED SETTINGS
    var csAppSettings101423 = {"csInitViewOptions": "1", "csViewOptions": "2", "csActiveHeaderBgColor": "#1976a6", "csActiveHeaderTxtColor": "#ffffff", "csInactiveHeaderBgColor": "#1976a6", "csInactiveHeaderTxtColor": "#ffffff", "csShowBullets": "True", "csShowAppHeader": "True"};


