        $(document).ready(function() {
            var DomainID = '12693';
            var PageID = '22391';
            var RenderLoc = '0';
            var MIID = '101591';

            //added to check to make sure moderated content doesn't bleed through the dialog
            if ($('#dialog-overlay-WindowLargeModal-body.moderateContent').length > 0) {
                $("#module-content-" + MIID).find(".ui-widget-detail").find(".ui-article").append("
 ");
            }
        });

	// APP SETTINGS

    // SETTINGS OBJECT WITH USER SAVED SETTINGS
    var csAppSettings101591 = {"csInitViewOptions": "1", "csViewOptions": "2", "csActiveHeaderBgColor": "#1976a6", "csActiveHeaderTxtColor": "#ffffff", "csInactiveHeaderBgColor": "#1976a6", "csInactiveHeaderTxtColor": "#ffffff", "csShowBullets": "True", "csShowAppHeader": "True"};


    // TURN HEADER ON IF NEEDED
    if(csAppSettings101591.csShowAppHeader == "True") {
        $("#pmi-108758 .ui-widget-header").removeClass("hidden");
    }

	$(document).ready(function() {
        csInit101591();

        $("#pmi-108758 .ui-widget.app.content-accordion .ui-article").each(function(){
        	$("p", this).addClass("ui-clear");
        });

	});

    function csInit101591() {
    	switch(csAppSettings101591.csInitViewOptions) {
        	case "1":
            	//nothing to be done here
            break;
            case "2":
            	$("#pmi-108758 .ui-widget.app.content-accordion .ui-article").addClass("active").attr("aria-expanded", "true");
                $("#pmi-108758 .ui-widget.app.content-accordion .ui-article .ui-article-detail").css("display","block");
            break;
            case "3":
            	$("#pmi-108758 .ui-widget.app.content-accordion li:eq(0) .ui-article").addClass("active").attr("aria-expanded", "true");
            	$("#pmi-108758 .ui-widget.app.content-accordion li:eq(0) .ui-article .ui-article-detail").css("display","block");
            break;
        }

        switch(csAppSettings101591.csViewOptions) {
        	case "1":
                $("#pmi-108758").on("click keydown", ".ui-article-title", function(e) {
                    if($(this).parent().hasClass("active")){
                        $(this).parent().find(".ui-article-detail").slideUp();
                        $(this).parent().removeClass("active");
                        $(this).attr("aria-expanded", "false");
                    } else {
                        $(this).parent().find(".ui-article-detail").slideDown();
                        $(this).parent().addClass("active");
                        $(this).attr("aria-expanded", "true");
                    }
                });
            break;
            case "2":
				$("#pmi-108758").on("click keydown", ".ui-article-title", function(e) {
                    if(allyClick(e)) {
                        e.preventDefault();

                        if($(this).parent().hasClass("active")){
                            $(this).parent().find(".ui-article-detail").slideUp();
                            $(this).parent().removeClass("active");
                            $(this).attr("aria-expanded", "false");
                        } else {
                            $("#pmi-108758 .ui-widget.app.content-accordion .active .ui-article-detail").slideUp();
                            $("#pmi-108758 .ui-widget.app.content-accordion .active").removeClass("active");
                            $(this).parent().find(".ui-article-detail").slideDown();
                            $(this).parent().addClass("active")
                            $(this).attr("aria-expanded", "true");
                        }
                    }
                });
            break;
        }

        $("#pmi-108758").on("click keydown", ".cs-accordion-expand-toggle", function(e) {
        	if(allyClick(e)) {
            	e.preventDefault();

                if($(this).hasClass("expand")) {
                	$(this).removeClass("expand").addClass("collapse").text("Collapse All").attr("aria-label", "Collapse All Accordion Items");
                	$("#pmi-108758 .ui-article-detail").slideDown();
                    $("#pmi-108758 .ui-article").addClass("active");
                    $("#pmi-108758 .ui-article-title").attr("aria-expanded", "true");
                } else {
                	$(this).removeClass("collapse").addClass("expand").text("Expand All").attr("aria-label", "Expand All Accordion Items");
                	$("#pmi-108758 .ui-article-detail").slideUp();
                    $("#pmi-108758 .ui-article").removeClass("active");
                    $("#pmi-108758 .ui-article-title").attr("aria-expanded", "false");
                }
            }
        });

        // ADD THE DYNAMIC STYLESHEET
        var dynStyleSheet = document.createElement('style');
        if(dynStyleSheet) {
            dynStyleSheet.setAttribute('type', 'text/css');
            var head = document.getElementsByTagName('head')[0];

            if(head) {
                head.insertBefore(dynStyleSheet, head.childNodes[0]);
            }

            // CHECK IF BULLETS ARE NEEDED
            var csBullets = "";
            if(csAppSettings101591.csShowBullets == "True") {
                csBullets = '#pmi-108758 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:after {' +
                                'content: "_";' +
                                'position: absolute;' +
                                'top: 4px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings101591.csActiveHeaderTxtColor + ';' +
                            '}' +
                            '#pmi-108758 .ui-widget.app.content-accordion .ui-article .ui-article-title:after {' +
                                'content: "+";' +
                                'position: absolute;' +
                                'top: 9px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings101591.csInactiveHeaderTxtColor + ';' +
                            '}';
            }

            var dynStyles = '#pmi-108758 .ui-widget.app.content-accordion .ui-widget-header {' +
                                'margin-bottom: 6px;' +
                            '}' +
                            '#pmi-108758 .ui-widget.app.content-accordion .ui-article {' +
                                'padding: 0px 0px 6px 0px;' +
                            '}' +
                            '#pmi-108758 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:before {' +
                                'content: "";' +
                                'position: absolute;' +
                                'bottom: -7px;' +
                                'left: 13px;' +
                                'width: 0px;' +
                                'height: 0px;' +
                                'border-style: solid;' +
                                'border-width: 7px 7px 0 7px;' +
                                'border-color: ' + csAppSettings101591.csActiveHeaderBgColor + ' transparent transparent transparent;' +
                            '}' +
                            '#pmi-108758 .ui-widget.app.content-accordion .ui-article.active .ui-article-title {' +
                                'background-color: ' + csAppSettings101591.csActiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings101591.csActiveHeaderTxtColor + ';' +
                                'border-radius: 1px;' +
                                '-moz-border-radius: 1px;' +
                                '-webkit-border-radius: 1px;' +
                                'position: relative;' +
                            '}' +
                            '#pmi-108758 .ui-widget.app.content-accordion .ui-article .ui-article-title {' +
                                'background-color: ' + csAppSettings101591.csInactiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings101591.csInactiveHeaderTxtColor + ';' +
                                'padding: 10px;' +
                                'position: relative;' +
                                'cursor: pointer;' +
                            '}' +
                            csBullets;

            var rules = document.createTextNode(dynStyles);

            if(dynStyleSheet.styleSheet){ // IE
                dynStyleSheet.styleSheet.cssText = dynStyles;
            } else {
                dynStyleSheet.appendChild(rules);
            }
        }
    }

    function allyClick(event) {
        if(event.type == "click") {
            return true;
        } else if(event.type == "keydown") {
            if(event.keyCode == 32 || event.keyCode == 13) {
                return true;
            }
        } else {
            return false;
        }
    }

