        $("#pmi-108708").on("click keydown", ".cs-accordion-expand-toggle", function(e) {
        	if(allyClick(e)) {
            	e.preventDefault();

                if($(this).hasClass("expand")) {
                	$(this).removeClass("expand").addClass("collapse").text("Collapse All").attr("aria-label", "Collapse All Accordion Items");
                	$("#pmi-108708 .ui-article-detail").slideDown();
                    $("#pmi-108708 .ui-article").addClass("active");
                    $("#pmi-108708 .ui-article-title").attr("aria-expanded", "true");
                } else {
                	$(this).removeClass("collapse").addClass("expand").text("Expand All").attr("aria-label", "Expand All Accordion Items");
                	$("#pmi-108708 .ui-article-detail").slideUp();
                    $("#pmi-108708 .ui-article").removeClass("active");
                    $("#pmi-108708 .ui-article-title").attr("aria-expanded", "false");
                }
            }
        });

        // ADD THE DYNAMIC STYLESHEET
        var dynStyleSheet = document.createElement('style');
        if(dynStyleSheet) {
            dynStyleSheet.setAttribute('type', 'text/css');
            var head = document.getElementsByTagName('head')[0];

            if(head) {
                head.insertBefore(dynStyleSheet, head.childNodes[0]);
            }

            // CHECK IF BULLETS ARE NEEDED
            var csBullets = "";
            if(csAppSettings101547.csShowBullets == "True") {
                csBullets = '#pmi-108708 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:after {' +
                                'content: "_";' +
                                'position: absolute;' +
                                'top: 4px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings101547.csActiveHeaderTxtColor + ';' +
                            '}' +
                            '#pmi-108708 .ui-widget.app.content-accordion .ui-article .ui-article-title:after {' +
                                'content: "+";' +
                                'position: absolute;' +
                                'top: 9px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings101547.csInactiveHeaderTxtColor + ';' +
                            '}';
            }

            var dynStyles = '#pmi-108708 .ui-widget.app.content-accordion .ui-widget-header {' +
                                'margin-bottom: 6px;' +
                            '}' +
                            '#pmi-108708 .ui-widget.app.content-accordion .ui-article {' +
                                'padding: 0px 0px 6px 0px;' +
                            '}' +
                            '#pmi-108708 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:before {' +
                                'content: "";' +
                                'position: absolute;' +
                                'bottom: -7px;' +
                                'left: 13px;' +
                                'width: 0px;' +
                                'height: 0px;' +
                                'border-style: solid;' +
                                'border-width: 7px 7px 0 7px;' +
                                'border-color: ' + csAppSettings101547.csActiveHeaderBgColor + ' transparent transparent transparent;' +
                            '}' +
                            '#pmi-108708 .ui-widget.app.content-accordion .ui-article.active .ui-article-title {' +
                                'background-color: ' + csAppSettings101547.csActiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings101547.csActiveHeaderTxtColor + ';' +
                                'border-radius: 1px;' +
                                '-moz-border-radius: 1px;' +
                                '-webkit-border-radius: 1px;' +
                                'position: relative;' +
                            '}' +
                            '#pmi-108708 .ui-widget.app.content-accordion .ui-article .ui-article-title {' +
                                'background-color: ' + csAppSettings101547.csInactiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings101547.csInactiveHeaderTxtColor + ';' +
                                'padding: 10px;' +
                                'position: relative;' +
                                'cursor: pointer;' +
                            '}' +
                            csBullets;

            var rules = document.createTextNode(dynStyles);

            if(dynStyleSheet.styleSheet){ // IE
                dynStyleSheet.styleSheet.cssText = dynStyles;
            } else {
                dynStyleSheet.appendChild(rules);
            }
        }
    }

    function allyClick(event) {
        if(event.type == "click") {
            return true;
        } else if(event.type == "keydown") {
            if(event.keyCode == 32 || event.keyCode == 13) {
                return true;
            }
        } else {
            return false;
        }
    }

