	// APP SETTINGS

    // SET INITIAL DEFAULT SETTINGS OBJECT - AN ERROR MAY OCCUR IF THIS ISN'T IN PLACE
    var csAppSettings101454 = {"csInitViewOptions": "1", "csViewOptions": "1", "csActiveHeaderBgColor": "#000000", "csActiveHeaderTxtColor": "#FFFFFF", "csInactiveHeaderBgColor": "#000000", "csInactiveHeaderTxtColor": "#FFFFFF", "csShowBullets": "True", "csShowAppHeader": "False"};


    // TURN HEADER ON IF NEEDED
    if(csAppSettings101454.csShowAppHeader == "True") {
        $("#pmi-108709 .ui-widget-header").removeClass("hidden");
    }

	$(document).ready(function() {
        csInit101454();

        $("#pmi-108709 .ui-widget.app.content-accordion .ui-article").each(function(){
        	$("p", this).addClass("ui-clear");
        });

	});

    function csInit101454() {
    	switch(csAppSettings101454.csInitViewOptions) {
        	case "1":
            	//nothing to be done here
            break;
            case "2":
            	$("#pmi-108709 .ui-widget.app.content-accordion .ui-article").addClass("active").attr("aria-expanded", "true");
                $("#pmi-108709 .ui-widget.app.content-accordion .ui-article .ui-article-detail").css("display","block");
            break;
            case "3":
            	$("#pmi-108709 .ui-widget.app.content-accordion li:eq(0) .ui-article").addClass("active").attr("aria-expanded", "true");
            	$("#pmi-108709 .ui-widget.app.content-accordion li:eq(0) .ui-article .ui-article-detail").css("display","block");
            break;
        }

        switch(csAppSettings101454.csViewOptions) {
        	case "1":
                $("#pmi-108709").on("click keydown", ".ui-article-title", function(e) {
                    if($(this).parent().hasClass("active")){
                        $(this).parent().find(".ui-article-detail").slideUp();
                        $(this).parent().removeClass("active");
                        $(this).attr("aria-expanded", "false");
                    } else {
                        $(this).parent().find(".ui-article-detail").slideDown();
                        $(this).parent().addClass("active");
                        $(this).attr("aria-expanded", "true");
                    }
                });
            break;
            case "2":
				$("#pmi-108709").on("click keydown", ".ui-article-title", function(e) {
                    if(allyClick(e)) {
                        e.preventDefault();

                        if($(this).parent().hasClass("active")){
                            $(this).parent().find(".ui-article-detail").slideUp();
                            $(this).parent().removeClass("active");
                            $(this).attr("aria-expanded", "false");
                        } else {
                            $("#pmi-108709 .ui-widget.app.content-accordion .active .ui-article-detail").slideUp();
                            $("#pmi-108709 .ui-widget.app.content-accordion .active").removeClass("active");
                            $(this).parent().find(".ui-article-detail").slideDown();
                            $(this).parent().addClass("active")
                            $(this).attr("aria-expanded", "true");
                        }
                    }
                });
            break;
        }

        $("#pmi-108709").on("click keydown", ".cs-accordion-expand-toggle", function(e) {
        	if(allyClick(e)) {
            	e.preventDefault();

                if($(this).hasClass("expand")) {
                	$(this).removeClass("expand").addClass("collapse").text("Collapse All").attr("aria-label", "Collapse All Accordion Items");
                	$("#pmi-108709 .ui-article-detail").slideDown();
                    $("#pmi-108709 .ui-article").addClass("active");
                    $("#pmi-108709 .ui-article-title").attr("aria-expanded", "true");
                } else {
                	$(this).removeClass("collapse").addClass("expand").text("Expand All").attr("aria-label", "Expand All Accordion Items");
                	$("#pmi-108709 .ui-article-detail").slideUp();
                    $("#pmi-108709 .ui-article").removeClass("active");
                    $("#pmi-108709 .ui-article-title").attr("aria-expanded", "false");
                }
            }
        });

        // ADD THE DYNAMIC STYLESHEET
        var dynStyleSheet = document.createElement('style');
        if(dynStyleSheet) {
            dynStyleSheet.setAttribute('type', 'text/css');
            var head = document.getElementsByTagName('head')[0];

            if(head) {
                head.insertBefore(dynStyleSheet, head.childNodes[0]);
            }

            // CHECK IF BULLETS ARE NEEDED
            var csBullets = "";
            if(csAppSettings101454.csShowBullets == "True") {
                csBullets = '#pmi-108709 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:after {' +
                                'content: "_";' +
                                'position: absolute;' +
                                'top: 4px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings101454.csActiveHeaderTxtColor + ';' +
                            '}' +
                            '#pmi-108709 .ui-widget.app.content-accordion .ui-article .ui-article-title:after {' +
                                'content: "+";' +
                                'position: absolute;' +
                                'top: 9px;' +
                                'right: 18px;' +
                                'width: 0px;' +
                                'color: ' + csAppSettings101454.csInactiveHeaderTxtColor + ';' +
                            '}';
            }

            var dynStyles = '#pmi-108709 .ui-widget.app.content-accordion .ui-widget-header {' +
                                'margin-bottom: 6px;' +
                            '}' +
                            '#pmi-108709 .ui-widget.app.content-accordion .ui-article {' +
                                'padding: 0px 0px 6px 0px;' +
                            '}' +
                            '#pmi-108709 .ui-widget.app.content-accordion .ui-article.active .ui-article-title:before {' +
                                'content: "";' +
                                'position: absolute;' +
                                'bottom: -7px;' +
                                'left: 13px;' +
                                'width: 0px;' +
                                'height: 0px;' +
                                'border-style: solid;' +
                                'border-width: 7px 7px 0 7px;' +
                                'border-color: ' + csAppSettings101454.csActiveHeaderBgColor + ' transparent transparent transparent;' +
                            '}' +
                            '#pmi-108709 .ui-widget.app.content-accordion .ui-article.active .ui-article-title {' +
                                'background-color: ' + csAppSettings101454.csActiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings101454.csActiveHeaderTxtColor + ';' +
                                'border-radius: 1px;' +
                                '-moz-border-radius: 1px;' +
                                '-webkit-border-radius: 1px;' +
                                'position: relative;' +
                            '}' +
                            '#pmi-108709 .ui-widget.app.content-accordion .ui-article .ui-article-title {' +
                                'background-color: ' + csAppSettings101454.csInactiveHeaderBgColor + ';' +
                                'color: ' + csAppSettings101454.csInactiveHeaderTxtColor + ';' +
                                'padding: 10px;' +
                                'position: relative;' +
                                'cursor: pointer;' +
                            '}' +
                            csBullets;

            var rules = document.createTextNode(dynStyles);

            if(dynStyleSheet.styleSheet){ // IE
                dynStyleSheet.styleSheet.cssText = dynStyles;
            } else {
                dynStyleSheet.appendChild(rules);
            }
        }
    }

    function allyClick(event) {
        if(event.type == "click") {
            return true;
        } else if(event.type == "keydown") {
            if(event.keyCode == 32 || event.keyCode == 13) {
                return true;
            }
        } else {
            return false;
        }
    }

