    var skrollrParalax = 0;

    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ChannelBarIsSticky": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.AppAccordion();
            this.Search();
            this.JsMediaQueries();           

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){  _this.StickyHeader(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            $("div.ui-widget.app.navigation li a").each(function() {
            	if(!$("span", this).length) {
                	$(this).wrapInner("");
            	}
            });

            $(".hidden-sections").remove(); /* moved from ChannelBar method by Matt B - causing missing breadcrumb issue - case#3164354 - 06.06.18 */
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },


        "StickyHeader": function() {
        	if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var channelBarOffSet = 83;
                        if ($(window).scrollTop() <= channelBarOffSet) {
                            if(this.ChannelBarIsSticky) {
                            	$("#gb-page").removeAttr("style");
                                $("header").removeClass("sticky").removeAttr("style");
                                $("nav").appendTo("#gb-header");
                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            if(!this.ChannelBarIsSticky) {
                                $("header").addClass("sticky");
                                $("#gb-page").css("padding-top", $("header").height()+"px");
                                $("nav").prependTo("#gb-header-links");
                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("header").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("#gb-page").removeAttr("style");
                            $("header").removeClass("sticky").removeAttr("style");
                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");

                        $(".hp-row.four").css("bottom", 0); 
                    break;
                }
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop":
                    // PARALAX
                    if(skrollrParalax == 0) {
                        skrollrParalax = skrollr.init({
                            "forceHeight" : false
                        });
                        skrollrParalax = 1;
                    }

                    if($("#sw-content-container10.region.ui-hp .rs-gallery-container").length) {
                        //DOC se height on mmr desc to keep content from jumping
                        var scrolled = 0;
                        var scrollLock = 0;
                        var loc = $("#hp-slideshow-outer").outerHeight();
                        $(window).scroll(function(e) {
                            scrolled = $(window).scrollTop();
                            if(scrolled >= loc && scrollLock === 0) {
                                $("#hp-slideshow .rs-gallery-desc").height($("#hp-slideshow .rs-gallery-desc").height());
                                scrollLock = 1;
                            } else if(scrolled < loc && scrollLock == 1){
                                $("#hp-slideshow .rs-gallery-desc").height("auto");
                                scrollLock = 0;
                            }
                        });
                    }
                break;
                case "1024":
                case "768":
                case "640":
                case "480":
                case "320":
                    // PARALAX
                    if(skrollrParalax == 1) {
                        skrollrParalax = 0;
                        skrollr.init().destroy();
                    }

                    if($("#hp-slideshow .rs-gallery-container").length) {
                        //DOC se height on mmr desc to keep content from jumping
                        var scrolled = 0;
                        var scrollLock = 0;
                        var loc = $("#hp-slideshow-outer").outerHeight();
                        $(window).scroll(function(e) {
                            scrolled = $(window).scrollTop();
                            if(scrolled >= loc && scrollLock === 0) {
                                $("#hp-slideshow .rs-gallery-desc").height($("#hp-slideshow .rs-gallery-desc").height());
                                scrollLock = 1;
                            scrollLock = 1;
                            } else if(scrolled < loc && scrollLock == 1){
                                $("#hp-slideshow .rs-gallery-desc").height("auto");
                                scrollLock = 0;
                            }
                        });
                    } 
                break;
            }
        },

        "MyStart": function() {
       	 	// ADD USER OPTIONS
            var userOptionsItems = "";

            	if($("#ui-btn-signin").length) {
                	userOptionsItems += "
" + $("#ui-btn-signin").html() + "";
                }

                if($("#ui-btn-register").length) {
                	userOptionsItems += "
" + $("#ui-btn-register").html() + "";
                }


            $("#cs-user-options-list").append(userOptionsItems);

            $("#ui-btn-mypasskey").addClass("flex-columns flex-items-center flex-justify-center").html('Passkeys');
            $("#sw-mystart-mypasskey").insertAfter($(".translate"));

            // REMOVE TAB INDEXES FROM SYSTEM  TAGS
            $("#cs-user-options-list a").removeAttr("tabindex");

        	// ADD SCHOOLS
            $("#cs-schools-list").html(''+$(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html()+'');


            $("#cs-resources-list").html($(".cs-header-links ul").html());

        	// ADD TRANSLATE
        	$(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                "type": 1, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                "languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"] - IF TRANSLATED LANGUAGE NAME IS EMPTY, THE ENGLISH NAME WILL BE USED
                    ["Spanish", "Español", "es"],
                    ["Afrikaans", "Afrikaans", "af"],
                    ["Albanian", "shqiptar", "sq"],
                    ["Amharic", "አማርኛ", "am"],
                    ["Arabic", "العربية", "ar"],
                    ["Armenian", "հայերեն", "hy"],
                    ["Azerbaijani", "Azərbaycan", "az"],
                    ["Basque", "Euskal", "eu"],
                    ["Belarusian", "Беларуская", "be"],
                    ["Bengali", "বাঙালি", "bn"],
