        "DropdownActions": function(params) {
            // FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                    $("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                    $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
            });

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        	if($(dropdownParent).hasClass("search")){
                                $("#gb-search-submit, #gb-search-input").attr("tabindex","-1");
                            }
                        } else {

                            $(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                            if($(dropdownParent).hasClass("search")){
                            	$("#gb-search-input").focus();
                                $("#gb-search-submit, #gb-search-input").attr("tabindex","0");
                            }
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                            $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey) {
                            e.preventDefault();

                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME ESC KEY
                    case template.KeyCodes.esc:
                        e.preventDefault();

                        // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(dropdownSelector).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
            	setTimeout(function () {
                	$(dropdownList + " a", this).attr("tabindex", "-1");
                  	$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                }, 100);

            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

        "Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/TX01918453/Centricity/Template/GlobalAssets/images///brands/Wbrand.png");
            var srcSplit = logoSrc.split("/");
            var srcSplitLen = srcSplit.length;
            if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                $("#gb-logo").append("").removeClass("hidden");
            } else {
                $("#gb-logo").append("").removeClass("hidden");
            }

            $.csHighContrastBar({
                "show": true,
                "alternateLogo": {
                    "selector": "", // ID OR CLASS OF DEFAULT LOGO 
                    "src": "", // ALTERNATE LOGO SRC
                    "alt": "" // ALTERNATE LOGO ALT TEXT
                },
                "toggle": function(on) {} // CUSTOM CALLBACK WHEN CONTRAST IS TOGGLED (ALSO FIRES ON DOC READY) - on PARAMETER IS EITHER true OR false (boolean)
            });


        },

        "StickyChannelBar": function() {
            var headerHeight = $("#gb-header").outerHeight();
            var navHeight = $("#gb-channel-list-outer").outerHeight();
            var lastScrollTop = 0;
            var windowHeight = $(window).height();

            $(document).scroll(function() {     
                distanceFromTop = $(this).scrollTop();

                if($(".ui-hp").length){
                    if(distanceFromTop > windowHeight){
                        $("body").addClass("scrolled-past-mmg");
                    } else {
                        $("body").removeClass("scrolled-past-mmg");
                    }
                }

                if(distanceFromTop >= 200){
                    $("#gb-page").addClass("sticky-header-prep");
                } else {
                    $("#gb-page").removeClass("sticky-header-prep");
                }

                if(distanceFromTop < lastScrollTop){
                    $("#gb-page").removeClass("idle").addClass("show-menu"); 
                } else {
                	$("#gb-page").addClass("idle").removeClass("show-menu"); 
                }
                lastScrollTop = distanceFromTop;

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                         $(".wcm-controls").css("margin-top", navHeight);
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + navHeight + "px"
                });
            });
        },

        "Body": function() {
            // FOR SCOPE
            var _this = this;

            //SHOW MENU WHEN USER BEGINS TO SCROLL UP
            var scrollTimer;

            scrollTimer = setTimeout(noScroll, 3000); // Initial start of timer
            $(window).bind('scroll', function(event) { // When scrolled
                  clearTimeout(scrollTimer); // Stop the existing timer
                  scrollTimer = setTimeout(noScroll, 3000); // And restart
            });

            function noScroll() {
                  $('#gb-page').addClass("idle").removeClass("show-menu");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

            // CHECK PAGELIST HEADER
            if($.trim($(".ui-widget.app.pagenavigation .ui-widget-header").text()) == "") {
                $(".ui-widget.app.pagenavigation .ui-widget-header").html("
Resources");
            }

            //REMOVE UNUSED LINKS
            $("[data-toggle='false'], #sw-content-layout-wrapper #sw-edit-page-button").remove();

        },

        "Homepage": function() {
            // FOR SCOPE
            var _this = this;

            if($("#gb-page.hp").length){
                //BUILD SHORTCUTS DROPDOWN
                if($("#hidden-shortcuts .siteshortcuts").length){
                    var shortcutsDDSelector = $("#hidden-shortcuts .siteshortcuts .ui-widget-header").text();
                    var shortcutsDD  =    '
' +
                                            '
'+shortcutsDDSelector+'' +
                                            '
' +
                                                '' + $("#hidden-shortcuts .site-shortcuts").html() + '' +
                                            '' +
                                        '';
                }

                // ADD SCHOOL LIST TO THE DOM   
                $(shortcutsDD).insertAfter(".cs-mystart-dropdown.schools");

                // BIND DROPDOWN EVENTS
                this.DropdownActions({
                    "dropdownParent": ".cs-mystart-dropdown.shortcuts",
                    "dropdownSelector": ".cs-selector",
                    "dropdown": ".cs-dropdown",
                    "dropdownList": ".cs-dropdown-list"
                });

                // STAY IN TOUCH WATERMARK
                var stayWatermarkSrc = jQuery.trim("/cms/lib/TX01918453/Centricity/Template/GlobalAssets/images///watermarks/district_watermark.png");
                var stayWatermarkSrcSplit = stayWatermarkSrc.split("/");
                var stayWatermarkSrcSplitLen = stayWatermarkSrcSplit.length;
                if((stayWatermarkSrc != "") && (stayWatermarkSrcSplit[stayWatermarkSrcSplitLen - 1] != "default-man.jpg")) {
                    $(".hp-column.one ").css("background-image", "url('"+stayWatermarkSrc+"')");
                } else {
                    $(".hp-column.one ").css("background-image", "url('/cms/lib/TX01918453/Centricity/Template/25/defaults/wylie-default-watermark.png')");
                }

                //MOVE UPCOMING EVENTS AND HEADLINES MORE LINKS
                if(this.GetBreakPoint() == "desktop" || this.GetBreakPoint() == "768"){
                    $(".hp-row.one .upcomingevents .view-calendar-link").appendTo(".hp-row.one .upcomingevents .ui-widget-header");
                    $(".hp-row.one .headlines .more-link").appendTo(".hp-row.one .headlines .ui-widget-header");
                }

                //ROW 2 HEADLINES
                $(".hp-row.two .headlines li").each(function(){
                    //WRAP HEADLINE TEXT
                    $(this).find(".ui-article-title, .ui-article-description, .ui-article-controls").wrapAll("

");
                    $(this).find(".ui-article-description").wrap("

");
                    $(this).find(".ui-article-description").attr("aria-hidden","true");
                })

                //HOVERS
                $(".hp-row.two .headlines li").hover(function() {
                    $(this).addClass("hover");
                    $(this).find(".ui-article-description").attr("aria-hidden","false");
                }, function() {
                    $(this).removeClass("hover");
                    $(this).find(".ui-article-description").attr("aria-hidden","true");
                });
                $(".hp-row.two .headlines .ui-read-more .more-link").hover(function() {
                    $(this).parent().addClass("hover");
                }, function() {
                    $(this).parent().removeClass("hover");
                });

                //FOCUS
                $(".hp-row.two .headlines li .ui-article-title a").focus(function() {
                    $(this).closest("li").addClass("hover");
                    $(this).closest("li").find(".ui-article-description").attr("aria-hidden","false");
                });

                $(".hp-row.two .headlines li .ui-article-title a").focusout(function() {
                    $(this).closest("li").removeClass("hover");
                    $(this).closest("li").find(".ui-article-description").attr("aria-hidden","true");
                });

                $(".hp-row.two .headlines .ui-read-more .more-link").focus(function() {
                    $(this).parent().addClass("hover");
                });

                $(".hp-row.two .headlines .ui-read-more .more-link").focusout(function() {
                    $(this).parent().removeClass("hover");
                });

                //CHECK HP-ROW THREE COLUMNS FOR APPS
                $(".hp-row.three .ui-hp").each(function(){
                    if($(this).find(".app").length > 0){
                        $(this).show();
                    }
                })
            }    

        },

        "Shortcuts": function() {
            var _this = this;

            // EDIT THESE TWO VARS
            var columnNums = [2, 2, 2, 1, 1]; // [960, 768, 640, 480, 320]
            var selector = ".hp-row.one div.ui-widget.app.siteshortcuts";

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(_this.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $(selector).each(function() {
                // RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).append($(".site-shortcuts-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
                var shortcutNum = $(".ui-widget-detail > ul.site-shortcuts > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                    // KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                        // IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                        // WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN

                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                           endRange = Math.round(shortcutNum / columnNum) + 1;
                       } else if((shortcutNum / columnNum) % 1 == 0.5 && i >= (columnNum / i)) {
                           endRange = Math.round(shortcutNum / columnNum) - 1;
                       } else {
                           endRange = Math.round(shortcutNum / columnNum)
                       }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.site-shortcuts > li:nth-child(n+1):nth-child(-n+" + endRange +")", this));
                    }
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).hide();
            });
        },

        "HeadlineImageCheck": function(){
            //FOR SCOPE
            var _this = this;

            //CHECK FOR HEADLINE ARTICLES THAT DO NOT HAVE IMAGES
            $(".hp-row.two .headlines").each(function(){
                $(this).find(".ui-articles li").each(function(){
                    if(!$(this).find(".ui-article-thumb img[src]").length) {
                        $(this).addClass("no-image");
                    }
                })
            })
        },

        "Footer": function() {
            // FOR SCOPE
            var _this = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-bb-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-bb-footer.copyright").append($("#sw-footer-copyright").html());

            //SET MAP CONTAINER SIZE
            var footerDifference = $(window).width() - $(".footer-row.contact .gb-section").width();
            var mapWidth = $(".footer-column.one").width() + (footerDifference / 2);
            $("#gb-map-container").css("width",mapWidth);

            // ADD FOOTER LOGO
            var footerLogoSrc = jQuery.trim("/cms/lib/TX01918453/Centricity/Template/GlobalAssets/images///footer_icons/district_footer.png");
            var footerLogoSrcSplit = footerLogoSrc.split("/");
            var footerLogoSrcSplitLen = footerLogoSrcSplit.length;
            if((footerLogoSrc != "") && (footerLogoSrcSplit[footerLogoSrcSplitLen - 1] != "default-man.jpg")) {
                $(".footer-row.one .footer-column.one").css("background-image", "url('"+footerLogoSrc+"')");
            } else {
                $(".footer-row.one .footer-column.one").css("background-image", "url('/cms/lib/TX01918453/Centricity/Template/25/defaults/wylie-default-footer-logo.svg')");
            }

            // ADD FOOTER BG IMAGE
            var footerBGImgSrc = jQuery.trim("/cms/lib/TX01918453/Centricity/Template/GlobalAssets/images///footer_background/esc_footer.jpg");
            var footerBGImgSrcSplit = footerBGImgSrc.split("/");
            var footerBGImgSrcSplitLen = footerBGImgSrcSplit.length;
            if((footerBGImgSrc != "") && (footerBGImgSrcSplit[footerBGImgSrcSplitLen - 1] != "default-man.jpg")) {
                $("footer").css("background-image", "url('"+footerBGImgSrc+"')");
            } else {
                $("footer").css("background-image", "url('/cms/lib/TX01918453/Centricity/Template/25/defaults/wylie-default-footer-bg.jpg')");
            }

            //REMOVE SITENAME & TAGLINE IF EMPTY OR NOT IN USE
            if($(".sitename-one").text().trim() == "" || $(".sitename-one").data("showSchoolName") === false){
                $(".sitename-one").remove();
            }

            if($(".sitename-tagline").text().trim() == "" || $(".sitename-tagline").data("showTagline") === false){
                $(".sitename-tagline").remove();
            }

            //BACK TO TOP
            $("#to-top").click(function(e){
                e.preventDefault();
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                            $("#rs-menu-btn").focus();
                        });
            });

            if($("#gb-footer-disclaimer span").text().trim() == ""){
                $("#gb-footer-disclaimer").hide();
            }

        },

