meta.script:
   function submitSearch()
	{
		if (document.getElementById('searchdata').value.length > 0)
		{
			var searchData = document.getElementById('searchdata').value;
			window.open('http://www.ruhs.k12.vt.us/search.php?searchdata=' + searchData + '&site_type=Main','popup','height=400,width=442,resizable=yes,status=no,menubar=no,toolbar=no,scrollbars=yes');
			return false;
		}
	}

body.script:
             $(".submitter").validate({
                submitHandler: function(form) {
                  var form_btn = $(form).find('button[type="submit"]');
                  var form_result_div = '#form-result';
				  var form_div = '#rtccform';
                  $(form_result_div).remove();
                  $(form_div).before('
');
                  var form_btn_old_msg = form_btn.html();
                  form_btn.html(form_btn.prop('disabled', true).data("loading-text"));
                  $(form).ajaxSubmit({

                    success: function(data) {

					  var response = data.split(",");

					  if( response[1] == 'true' ) {
                        $(form).find('.form-control').val('');
                      }
                      form_btn.prop('disabled', false).html(form_btn_old_msg);
                      //$(form_result_div).html(response[0]).fadeIn('slow');
                      $('.success-result').html(response[0]).fadeIn('slow');
                      setTimeout(function(){ $('.success-result').fadeOut('slow') }, 10000);
					  if( response[1] == 'true' ) {
					     $(form_div).html("

Thank you for submitting your application

");
					  }	
					  grecaptcha.reset();
                    }
                  });
                }
              });


                      $('.time-picker').timepicker({
