			"numOfDropdowns" : 2,
			"dropdownSelectorText" : ['Elementary Schools', 'Secondary Schools'],
			"keywordsInDropdowns" : [["Elementary","1"],["Middle","2"],["High","2"]]	
		});
		//DOC reset dropdown functions
		$(".sw-mystart-dropdown").click(function() { $(".sw-dropdown",this).show(); });
		$(".sw-mystart-dropdown").hover(function() {}, function() { $(".sw-dropdown",this).hide(); });
		//DOC move mystart buttons
		$(".sw-mystart-button, #sw-mystart-account").appendTo($("#sw-mystart-left"));
		//DOC fix spaces for search
		$("#sw-mystart-search").unbind();

