                ["Portuguese", "português", "pt"],
                ["Russian", "русский", "ru"],
                ["Ukrainian", "ÑƒÐºÑ€Ð°Ñ—Ð½ÑÑŒÐºÐ¸Ð¹", "uk"]
            ],
