        $(".sw-mystart-dropdown.translate ul").addClass("sw-dropdown-list");

		// ********** HEADER ********** //
		if($("#gb-school-address h1:contains('Elementary')").length) {
			var siteName = $("#gb-school-address h1").text();
			var schoolName = siteName.split("Elementary");
			$("#gb-school-address h1").text($.trim(schoolName[0]));
			$("#gb-school-address h2").text("Elementary School");
			$("#gb-school-address h1").show();
        	} else if($("#gb-school-address h1:contains('Middle')").length) {
			var siteName = $("#gb-school-address h1").text();
			var schoolName = siteName.split("Middle");
			$("#gb-school-address h1").text($.trim(schoolName[0]));
			$("#gb-school-address h2").text("Middle School")
			$("#gb-school-address h1").show();
        	} else if($("#gb-school-address h1:contains('High')").length) {
			var siteName = $("#gb-school-address h1").text();
			var schoolName = siteName.split("High");
			$("#gb-school-address h1").text($.trim(schoolName[0]));
			$("#gb-school-address h2").text("High School")
			$("#gb-school-address h1").show();
        	} else if($("#gb-school-address h1:contains('Foundation')").length) {
			var siteName = $("#gb-school-address h1").text();
			var schoolName = siteName.split("Public");
			$("#gb-school-address h1e").text($.trim(schoolName[0]));
			$("#gb-school-address h2").text("Public Schools Foundation");
			$("#gb-school-address h1").show();
        	} else if($("#gb-school-address h1:contains('Auburn School District')").length) {
        		$("#gb-school-address h2").remove();
        	}	

