        	$(".rs-menu-option .rs-menu-option-sublist > li a span").each(function() {
        		if($(this).text() == "Translate") {
        			$(this).parent().attr("href", "//translate.google.com/#en/es/"+document.location.href);
        		}
        	});

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
