$('.social-tabs li').first().addClass('active');
$('.social-tab li').first().addClass('active');

$("ul.social-tabs li a").click(function(e){
    e.preventDefault();

    if (!$(this).parent('li').hasClass("active")) {
        var tabNum = $(this).parent('li').index();
        var nthChild = tabNum+1;
        $("ul.social-tabs li.active").removeClass("active");
        $(this).parent('li').addClass("active");
        $("ul.social-tab li.active").removeClass("active");
        $("ul.social-tab li:nth-child("+nthChild+")").addClass("active");

        $('ul.social-tabs').attr('class', 'social-tabs');
        var currentTabClass = $(this).parent('li').attr('class');
        $('ul.social-tabs').addClass( currentTabClass );
    }
});

