		function sliderCaptions (){

		var sliderH = $('.nivoSlider').outerHeight();
		var captionH = $('.nivo-caption').outerHeight();

		if( sliderH + captionH <= 500 && $('.nivoSlider, .bannerContainer').css('max-height') == '500px' ){

		$('.nivoSlider, .bannerContainer').css('max-height','none');
		$('.nivoSlider').css('padding-bottom', captionH);

		} else if ( sliderH + captionH <= 500 && $('.nivoSlider, .bannerContainer').css('max-height') == 'none' ){

		$('.nivoSlider').css('padding-bottom', captionH);

		} else {

		$('.nivoSlider').css('padding-bottom', '0');
		$('.nivoSlider, .bannerContainer').css('max-height','500px');

		}
		}


