	$(window).load(function() {
    	$('#sw-mystart-outer').remove();
        //-- Added bullets
        $('.navigation li .bullet').each(function(k, v){
        	if($(v).hasClass('expandable') || $(v).hasClass('collapsible')){
            	$(v).append(navSVG);
            }
        });
        $('.sp.column.one .navigation a').attr("tabindex", "0");
		calendarListView();
		searchText();
        var barOff = $('#gb-channel-list-outer').offset(),
            barH = $('#gb-channel-list-outer').height();
        var wScroll = $(window).scrollTop(),
            vpw = $(window).width();
        if(wScroll >= barOff.top && vpw > 1023){
            $('#gb-channel-list-outer').addClass('fixed');
            $('#gb-header-outer').css('margin-bottom', barH + 'px');
        }
        $(window).on('scroll', function(){
            var wScroll = $(window).scrollTop(),
                vpw = $(window).width();
            if(wScroll >= barOff.top && vpw > 1023){
                $('#gb-channel-list-outer').addClass('fixed');
                $('#gb-header-outer').css('margin-bottom', barH + 'px');
                if($('.hp').size() > 0 && vpw > 1200){
                	$('.gb-school-dropdown').slideUp().attr('aria-expanded', 'false').attr('aria-');
                    $('#gb-school-list').removeClass('open');
                }
            } else if(wScroll < barOff.top && vpw > 1023){
                $('#gb-channel-list-outer').removeClass('fixed');
                $('#gb-header-outer').css('margin-bottom', '0px');
                if($('.hp').size() > 0 && vpw > 1200){
                	$('.gb-school-dropdown').slideDown();
                    $('#gb-school-list').addClass('open');
                }
            }
        });
        resize();

        csGlobalJs.OpenInNewWindowWarning();
	});

    $(window).on('resize', function(){
    	resize();
    });

    function resize(){
    	var vpw = $(window).width();
        if(vpw <= 1200 && $('.hp').size() > 0 && vpw >= 1024){
            $('.gb-school-dropdown').slideUp();
            $('#gb-school-list').removeClass('open');
        } else if(vpw > 1200 && $('.hp').size() > 0){
            $('.gb-school-dropdown').slideDown();
            $('#gb-school-list').addClass('open');
        }
        if(vpw < 1024){
        	$('#hp-content-outer').prepend($('#gb-school-list'));
            $('#hp-content, #gb-footer').addClass('cs-fw');
            $('.gb-school-info-outer').append($('#gb-footer-options'));
            $('#gb-footer-options').prepend($('.passkeys'));
            if($('.key-icon').size() == 0){
            	$('#ui-btn-mypasskey').append(keySVG);
                $('#ui-btn-mypasskey span').addClass('acc-hidden');
           	}
            $('.gb-social-outer').addClass('cs-jc');
            $('#cs-sw-footer').removeClass('cs-jsb').addClass('cs-jc');
            if($('.hp.column.two .ui-widget-header svg').size() == 0){
            	$('.hp.column.two .ui-widget-header').append(mobileSVG);
            }
            $('.hp.column.two .ui-widget-header').unbind('click').click(function(){
            	if($(this).hasClass('open')){
                	$(this).removeClass('open');
                    $(this).siblings('.ui-widget-detail').slideUp();
                    $(this).parent().removeClass('active');
                } else {
                	$(this).addClass('open');
                    $(this).siblings('.ui-widget-detail').slideDown();
                    $(this).parent().addClass('active');
                }
            });
            $('#gb-header-left').removeClass('cs-fw cs-jc');
            $('.gb-school-info-outer, #sw-info-container').removeClass('cs-fw');
            if($('#calendar-btn-change-month').size() > 0){
                $('#calendar-btn-change-month').click();
            }
            if(vpw < 640){
            	$('#gb-header-left').addClass('cs-fw cs-jc');
                $('.gb.footer.one').append($('#gb-footer-options'));
                	$('.gb-school-info-outer, #sw-info-container').removeClass('cs-fw');
                    if($('#ui-btn-calendarlist-list').size() > 0){
                    	$('#ui-btn-calendarlist-list').click();
                    }
                if(vpw < 480){
                	$('.gb-school-info-outer, #sw-info-container').addClass('cs-fw');
                }
            }
        } else {
        	$('#gb-channel-list').append($('#gb-school-list'));
            $('#hp-content, #gb-footer').removeClass('cs-fw');
            $('#cs-sw-footer').prepend($('#gb-footer-options'));
            $('.gb-social-outer').removeClass('cs-jc');
            $('#cs-sw-footer').addClass('cs-jsb').removeClass('cs-jc');
            $('#gb-header-left').removeClass('cs-fw');
            $('.hp.column.two .ui-widget.app').removeClass('active');
            $('.hp.column.two .ui-widget-header').removeClass('open');
            $('.hp.column.two .ui-widget-detail').show();
            $('#ui-btn-mypasskey .key-icon').remove();
            $('#ui-btn-mypasskey span').removeClass('acc-hidden');
            $('#gb-mystart-inner').prepend($('.passkeys'));
        }
    }

