        $(document).on("click keydown", ".gb-custom-dropdown", function(e) {
        	if(allyClick(e)) {
            	e.preventDefault();

                if($(this).hasClass('open')){
                    $(this).removeClass('open');
                    $('.sw-dropdown', this).stop().slideUp().attr('aria-epxanded', 'false').attr('aria-hidden', 'true');
                } else {
                	$(this).addClass('inner');
                    $(this).addClass('open');
                    $('.sw-dropdown', this).stop().slideDown().attr('aria-expanded', 'true').attr('aria-hidden', 'false');
                    $('.sw-dropdown a', this).attr('tabindex', '-1');
                    $('.sw-dropdown a', this).first().addClass('first').attr('tabindex', '0').focus();
                    $('.sw-dropdown li', this).first().addClass('first');
                    $('.sw-dropdown a', this).last().addClass('last');
                }
            }
        });

        $(document).on("click keydown", ".gb-custom-dropdown a", function(e) {
        	e.stopImmediatePropagation();
        });

        $(document).on("focusout", ".gb-custom-dropdown", function(e) {
        	var _this = this; 

        	setTimeout(function() {
                if(!$(_this).is(":focus") && !$(_this).find(":focus").length) {
                    $(_this).removeClass('open');
                	$(".sw-dropdown", _this).stop().slideUp().attr('aria-epxanded', 'false').attr('aria-hidden', 'true');
                }
            }, 500);
        }).on("mouseleave", ".gb-custom-dropdown", function(e) {
        	$(this).removeClass('open');
            $(".sw-dropdown", this).stop().slideUp().attr('aria-epxanded', 'false').attr('aria-hidden', 'true');
        });

        $('.sw-dropdown a').keydown(function(e){
        	e.stopPropagation();
        	var c = e.which,
            	first = $(this).parent().siblings('.first').children('.first'),
            	next = $(this).parent().next().children(),
                prev = $(this).parent().prev().children();
            if((c == kDown && !($(this).hasClass('schools')))|| (c == kUp && !($(this).hasClass('schools')))){
                e.preventDefault();
                if(c == kUp && $(this).hasClass('first')){
                    $(this).closest('.inner').focus();
                }
                if(c == kDown && $(this).hasClass('last')){
                	first.focus();
                } else if(c == kDown){
                    next.focus();
                } else {
                    prev.focus();
                }
            }
        });

