$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=0LThFQoxiJa%2bFdKNch4WTA%3d%3d&SessionID=eb705a08-1433-4531-9da2-51c9a6ceda5b&SiteID=4&ChannelID=1615&SectionID=1855&PageID=8944&HitDate=4%2f29%2f2020+1%3a25%3a47+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.11.125';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
