            }
        }).keydown(function(e){
        	var c = e.which;
            if(c == kEnter || c == kSpace){
            	e.preventDefault();
                if($(this).hasClass('open')){
                    $('.gb-school-dropdown').stop().slideUp();
                    $(this).removeClass('open').removeClass('inner');
                    $('.gb-school-dropdown li a').first();
                } else {
                    $('.gb-school-dropdown').slideDown();
                    $('.gb-school-dropdown li a').first().focus().addClass('first');
