		        	"galleryOverlay" 		: "no",
		       		"linkedElement" 		: ["image"], // ["image", "title", "overlay", "all"]
		        	"playPause" 			: "yes",
		        	"mainControls" 			: "yes",
		        	"contMoreLink" 			: "no",
		        	"contMoreLinkText" 		: "Read More",
		        	"descMoreLink" 			: "no",
		        	"descMoreLinkText" 		: "Read More",
		        	"bullets" 			: "yes",
		        	"bulletControls" 		: "no",
		        	"thumbnails" 			: "no",
		        	"thumbnailWidth" 		: 122,
		        	"thumbnailHeight" 		: 53,
		        	"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
		        	"hoverPause" 			: "yes",
		        	"fullScreenRotator" 		: "no", // image dimensions work well at 1500x915
		        	"fullScreenBreakpoints"   	: ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		        	"fullScreenVideo" 		: "no",
		        	"transitionType" 		: "slide", // fade or slide
		        	"transitionSpeed" 		: 3,
		        	"transitionDelay" 		: 8,
				"autoRotate" 			: "yes",
		        	"onImageLoad" 			: function() { },
		        	"allImagesLoaded" 		: function() { },
		        	"onThumbnailLoad" 		: function() { },
		        	"allThumbnailsLoaded" 		: function() { },
		        	"onTransitionStart" 		: function() { },
		        	"onTransitionEnd" 		: function() {
						var vpw = $(window).width();
                        if(vpw > 1023){
                            setTimeout(function(){
                                if($('.rs-caption.active p').text().length == 0 && $('#hp-caption-toggle').hasClass('open')){
                                    $('#gb-caption-wrapper').hide().attr('aria-hidden', 'true');
                                    $('#gb-caption-wrapper .close-icon').attr('tabindex', '-1');
                                    $('.rs-gallery-container').addClass('lock');
                                    $('.rs-gallery-images').addClass('lock');
                                } else if($('.rs-caption.active').text().length == 0){
                                    $('#hp-caption-toggle').hide().attr('aria-hidden', 'true').attr('tabindex', '-1');
                                } else if($('.rs-caption.active').text().length != 0 && $('#hp-caption-toggle').hasClass('open')){
                                    $('#gb-caption-wrapper').show().attr('aria-hidden', 'false');
                                    $('#gb-caption-wrapper .close-icon').attr('tabindex', '0');
                                    $('.rs-gallery-container').removeClass('lock');
                                } else if($('.rs-caption.active').text().length != 0){
                                    $('#hp-caption-toggle').show().attr('aria-hidden', 'false').attr('tabindex', '0');
                                }
                            }, 200);
                        }
                    },
		        	"watchVidClick" 		: function() { },
		        	"closeVidClick" 		: function() { },
		        	"allLoaded" 			: function() { 

						var vpw = $(window).width();
                        if(vpw > 1023){
                            var b = ''+ navSVG +'';
                            $('.rs-gallery-control.next, .rs-gallery-control.back').append(navSVG);
                            $('.rs-gallery-container').prepend(b);
                            $('.rs-gallery-caption').wrap('
');
                            $('.rs-gallery-caption').append(closeSVG);

                            $('#hp-caption-toggle').click(function(){
                                if($(this).hasClass('open')){
                                    $(this).removeClass('open').slideDown().attr('aria-hidden', 'false').attr('aria-label', 'Show Caption').attr('tabindex', '0');
                                    $('#gb-caption-wrapper').slideUp().attr('aria-hidden', 'false');
                                    $('.close-icon').attr('aria-hidden', 'true').attr('tabindex', '-1');
                                } else {
                                    $(this).addClass('open').slideUp().attr('aria-hidden', 'true').attr('tabindex', '-1');
                                    $('#gb-caption-wrapper').slideDown(500, function(){$('.close-icon', this).attr('aria-hidden', 'false').attr('aria-label', 'Hide Caption').attr('tabindex', '0').focus();}).attr('aria-hidden', 'false');
                                }
                            }).keydown(function(e){
                                var c = e.which;
                                if(c == kEnter || c == kSpace){
                                    e.preventDefault();
                                    $(this).click();
                                }
                            });
                            $('.close-icon').click(function(){
                                if($('#hp-caption-toggle').hasClass('open')){
                                    $('#hp-caption-toggle').removeClass('open').slideDown().attr('aria-hidden', 'false').attr('tabindex', '0');
                                    $('#gb-caption-wrapper').slideUp().attr('aria-hidden', 'false');
                                    $('.close-icon').attr('aria-hidden', 'true').attr('tabindex', '-1');
                                } else {
                                    $('#hp-caption-toggle').addClass('open').slideUp().attr('aria-hidden', 'true').attr('tabindex', '-1');
                                    $('#gb-caption-wrapper').slideDown().attr('aria-hidden', 'false');
                                }
                            }).keydown(function(e){
                                var c = e.which;
                                if(c == kEnter || c == kSpace){
                                    if($('#hp-caption-toggle').hasClass('open')){
                                        $('#hp-caption-toggle').removeClass('open').slideDown(500, function(){$('#hp-caption-toggle').focus();}).attr('aria-hidden', 'false').attr('tabindex', '0');
                                        $('#gb-caption-wrapper').slideUp().attr('aria-hidden', 'false');
                                        $('.close-icon').attr('aria-hidden', 'true').attr('tabindex', '-1');
                                    } else {
                                        $('#hp-caption-toggle').addClass('open').slideUp().attr('aria-hidden', 'true').attr('tabindex', '-1');
                                        $('#gb-caption-wrapper').slideDown().attr('aria-hidden', 'false');
                                    }
                                }
                            });
                            $('.rs-gallery-images').hover(function(){
                                if($('.rs-caption.active p').text().length != 0 ){
                                    $('#hp-caption-toggle').addClass('open').stop().slideUp().attr('aria-hidden', 'true').attr('tabindex', '-1');
                                    $('#gb-caption-wrapper').stop().slideDown().attr('aria-hidden', 'false');
                                }
                            }, function(){
                                if($('.rs-caption.active p').text().length != 0 ){
                                    $('#hp-caption-toggle').removeClass('open').slideDown().attr('aria-hidden', 'false').attr('tabindex', '0');
                                    $('#gb-caption-wrapper').slideUp().attr('aria-hidden', 'true');
                                }
                            });
                        }

                        $("#hp-slideshow .rs-gallery-control.play-pause").insertBefore($("#hp-slideshow .rs-gallery-bullets-outer"));
                    }
			});
		}
	}

	function appView() {
		if(!appViewSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			if(!$("#hp-content div.ui-widget.app.active").length){
				$("#hp-content div.ui-widget.app:first").addClass("active");
			}

			// DO NOT ADD APP INTO ACCORDION IF IT DOES NOT HAVE A HEADER
			$("#hp-content div.ui-widget.app").each(function() {
				if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
					$(this).addClass("no-header");
				}
			});

			// ACCORDION HEADER CLICK
			$("#hp-content div.ui-widget-header").click(function(){
				var clickedApp = $(this).parent().parent().attr("id");

				$("#hp-content div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");

				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			});

			appViewSet = 1;
		}
	}

	function appViewReset() {
		if(appViewSet) {
			$("#hp-content div.ui-widget-header").unbind("click");

			appViewSet = 0;
		}
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//ADD TRANSLATOR
		addFramesetTranslate();
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");

		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		//DOC change schoollist selector text
		$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

		//ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
		$(".sw-mystart-dropdown").addClass("transition");

		//ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
		$(".sw-mystart-button").addClass("transition");



		// ********** HEADER ********** //
		// ADD LOGO
		var logoSrc = jQuery.trim("/cms/lib/WI01001868/Centricity/Template/GlobalAssets/images///Logos/Final-Logo2.png");
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
			$("#gb-logo").append('').removeClass("hidden");
		} else {
			$("#gb-logo").append('').removeClass("hidden");
		}

        //-- Move Passkeys
        if($('a#ui-btn-mypasskey').size() > 0){
        	var passkey = '

' + $('#sw-mystart-mypasskey').html() + lShadow + rShadow + '';
        	$('#gb-mystart-inner').prepend(passkey);
            $('#sw-mystart-mypasskey').remove();
            $('#ui-btn-mypasskey span').text('Passkeys');
        }

        //-- Added Sign in and Regist Links
        if($('#ui-btn-signin').size() > 0){
        	$('.gb-custom-button.signin a').attr('href', $('#ui-btn-signin a').attr('href'));
        }
        if($('#ui-btn-register').size() > 0){
        	$('.gb-custom-button.register a').attr('href', $('#ui-btn-register a').attr('href'));
        }

        //-- Get District Btn Link
        $('.gb-custom-button.district a').attr('href', $('.sw-mystart-button.home a').attr('href'));

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");

		if($(".sw-mystart-button.pw").length) {
			var myviewBtn = $(".sw-mystart-button.pw").html();
			$("
" + myviewBtn + "").prependTo("#sw-myaccount-list");
			$(".sw-mystart-button.pw").hide();
		}
		if($(".sw-mystart-button.manage").length) {
			var manageBtn = $(".sw-mystart-button.manage").html();
			$("
" + manageBtn + "").prependTo("#sw-myaccount-list");
			$(".sw-mystart-button.manage").hide();
		}

        //-- Move User Options
        $('.gb-custom-dropdown.options').append('' + $('#sw-myaccount-list').html() + '');

        //-- Move Search
        $('#gb-header-right').append($('#sw-mystart-search'));
        $('#sw-search-button span').html('');
        //$('#sw-mystart-search').prepend('Search Input');
        //$('#sw-search-input').attr('aria-labelledby', 'search-label');
        $('#sw-search-button').attr('aria-label', 'Submit Search').attr('role', 'button').keydown(function(e){
        	var c = e.which;
            if(c == kEnter || c == kSpace){
            	e.preventDefault();
            	$(this).click();
            }
