	function switchDip_713345(num,pause){
		clearTimeout(slideshowTimer_713345);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_713345();
		var speed = slideshowMotionSpeed_713345();
		var currentNum = document.getElementById('currentNum_713345').value;
		var totalDips = document.getElementById('totalDips_713345').value;
		var video = document.getElementById('video_713345').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_713345(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_713345' + nums).length){
				$('#nav_713345' + nums).removeClass('on');
			}
			
			if($('#nav_li_713345' + nums).length){
				$('#nav_li_713345' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_713345' + nums).length){
					$('#videoShell_713345' + nums).hide();
				}
				$('#dipImage_713345' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_713345' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_713345' + nums).length){
				$('#videoShell_713345' + nums).fadeOut(speed);
			}
			if($('#dipContent_713345' + nums).length){
				$('#dipContent_713345' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_713345' + nums).length){
				$('#nav_713345' + nums).addClass('on');
			}
			
			if($('#nav_li_713345' + nums).length){
				$('#nav_li_713345' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_713345' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_713345' + nums).length){
					$('#videoShell_713345' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_713345' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_713345' + nums).length){
					$('#videoShell_713345' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_713345' + nums).length){
				$('#dipContent_713345' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_713345(timer);
			document.getElementById('currentNum_713345').value = nums;
		}
	}
	
	function slideshowMotion_713345(){
