    $(function() {
        CreativeTemplate.Init(); //tip of the iceberg
    });

    var csTemplate = {};

    var CreativeTemplate = {

        /*****
            PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "",
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,
        "TranslateCookieExists": false,
        "TranslateCookieValue": "",


        /*****
            METHODS
        *****/

// BIG BANG METHOD
        "Init": function() {
            // FOR SCOPE
            var _this = this;
            window.csTemplate = this;

            this.SetTemplateProps();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.JsMediaQueries();
            this.GlobalIcons();
            this.MyStart();
            this.Search();
            this.Header();
            this.ChannelBar();
            this.MegaMenu();
            this.StickyChannelBar();
            this.Body();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();

            this.BuildStylesheet();

            $(window).load(function(){ window.csTemplate.WindowLoad(); });
            $(window).resize(function(){ 
                 window.csTemplate.WindowResize(); 
                 window.csTemplate.CalendarListView();
                 window.csTemplate.StickyChannelBar();
            });
            $(window).scroll(function(){  
              		window.csTemplate.StickyHeader(); 
                    window.csTemplate.StickyChannelBar();

                    if($(window).scrollTop() > 300){
                        $("#gb-scroll-up").fadeIn();
                    } else {
                         $("#gb-scroll-up").fadeOut();
                    }
            });

            //REMOVE DUPLICATE IDs
            $("#sw-mystart-left #ui-btn-myview").remove();

            // WATCH GOOGLE TRANSLATE COOKIE STATE
            window.setInterval(function() {
            	_this.CheckTranslateCookie()
            }, 500);

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            if($(window).width() >= 320 && $(window).width() <= 639){
                this.CalendarListView();
            }    
            this.Search();

            $("div.ui-widget.app.navigation li div.bullet").click(function(){
                return true;
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-mystart-dropdown.translate").unbind('hover');
            $(".sw-mystart-dropdown.translate").hover(function(e){

            }, function(e) {
                //e.stopPropagation();
                $(".sw-dropdown-arrow").removeClass("rotate-arrow");
                $(this).removeClass("open");
                $(".sw-dropdown",this).slideUp("slow", "swing").attr("aria-hidden", "true");
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $("#sw-mystart-account").unbind('hover');
            $("#sw-mystart-account").hover(function(){

            }, function() {
                $(".icon-font-arrow",this).removeClass("open");
                $("#cs-sw-myaccount-list",this).slideUp("slow", "swing").attr("aria-hidden", "true");
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
           channelHoverTimer = "";
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                //$(".sw-channel-item ul").stop(true, true);
                	var thisChannel = this;
                    var subList = $(this).children('ul');
                    channelHoverTimer = setTimeout(function(){
                        if ($.trim(subList.html()) !== "") {
                            subList.slideDown(300, "swing");
                        }
                        $(thisChannel).addClass("hover");
                    }, 500);
            }, function(){
            	var thisChannel = this;
                $(".sw-channel-item ul").stop(true, true);
                clearTimeout(channelHoverTimer);
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(thisChannel).removeClass("hover");
            });

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            //this.MegaMenu();

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            this.StickyHeader();
        },
        "StickyHeader": function() {


            switch(this.GetBreakPoint()) {
                case "desktop": 
                	var mystartHeight = $('#gb-mystart-outer').height();
                    var headerHeight = $('#gb-header-outer').height();
                    var channelHeight = $('#gb-channel-list-outer').height();
                    if($(window).scrollTop() >= mystartHeight + headerHeight + 25) {
                      $("#gb-channel-list-outer").addClass("fixed");
                      $(".sp main").css("margin-top",channelHeight);

                    } else if($(window).scrollTop() < headerHeight + mystartHeight + 25) {
                        $("#gb-channel-list-outer").removeClass("fixed");
                        $(".sp main").css("margin-top","0px");
                    }
                break;
                case "768":
                case "640":
                case "480":
                case "320":
                break;
            }

        },

//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {

        	var template = this;

            switch(this.GetBreakPoint()) {
                case "desktop": 
                	$("#sw-mystart-search").appendTo("#gb-search-container");
                    $("#hp-content-two .ui-widget.app.upcomingevents .ui-articles li:gt(5)").remove();

                break;
                case "768":
                	$("#sw-mystart-search").appendTo("#gb-channel-list");
                    var searchBtn = ''
                    $("#gb-channel-list").append(searchBtn);
                    $("#hp-content-two .ui-widget.app.upcomingevents .ui-articles li:gt(2)").remove();
                    var scrollBtn = '';
                    $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").prepend(scrollBtn);

                    $("#gb-channel-list #gb-search-btn").on("click keydown", function(e) {
                        searchHoverTimer = "";
                        if(template.AllyClick(e)) {
                            e.preventDefault();
                            if($("#sw-mystart-search").hasClass("open")){
                            clearTimeout(searchHoverTimer);
                                $("#sw-mystart-search").removeClass("open").attr("aria-hidden","true");
                                    $(this).attr("aria-expanded","false").focus();
                            } else {
                                $("#sw-mystart-search").addClass("open").attr("aria-hidden","false");
                                searchHoverTimer = setTimeout(function(){
                                    $("#gb-channel-list #sw-search-input").focus().attr("tabindex","0");
                                    $(this).attr("aria-expanded","true");
                                }, 1000);
                            }
                        }
                    });
                break;
                case "640":
                	$("#sw-mystart-search").appendTo("#gb-channel-list");
                    var searchBtn = ''
                    $("#gb-channel-list").append(searchBtn);
                    $("#hp-content-two .ui-widget.app.upcomingevents .ui-articles li:gt(2)").remove();
                    var scrollBtn = '';
                    $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").prepend(scrollBtn);
                    $("#gb-channel-list #gb-search-btn").on("click keydown", function(e) {
                        searchHoverTimer = "";
                        if(template.AllyClick(e)) {
                            e.preventDefault();
                            if($("#sw-mystart-search").hasClass("open")){
                            clearTimeout(searchHoverTimer);
                                $("#sw-mystart-search").removeClass("open").attr("aria-hidden","true");
                                    $(this).attr("aria-expanded","false").focus();
                            } else {
                                $("#sw-mystart-search").addClass("open").attr("aria-hidden","false");
                                searchHoverTimer = setTimeout(function(){
                                    $("#gb-channel-list #sw-search-input").focus().attr("tabindex","0");
                                    $(this).attr("aria-expanded","true");
                                }, 1000);
                            }
                        }
                    });
                break;
                case "480":
                	$("#sw-mystart-search").appendTo("#gb-channel-list");
                    var searchBtn = ''
                    $("#gb-channel-list").append(searchBtn);
                    $("#hp-content-two .ui-widget.app.upcomingevents .ui-articles li:gt(2)").remove();
                    var scrollBtn = '';
                    $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").prepend(scrollBtn);
                    $("#gb-channel-list #gb-search-btn").on("click keydown", function(e) {
                        searchHoverTimer = "";
                        if(template.AllyClick(e)) {
                            e.preventDefault();
                            if($("#sw-mystart-search").hasClass("open")){
                            clearTimeout(searchHoverTimer);
                                $("#sw-mystart-search").removeClass("open").attr("aria-hidden","true");
                                    $(this).attr("aria-expanded","false").focus();
                            } else {
                                $("#sw-mystart-search").addClass("open").attr("aria-hidden","false");
                                searchHoverTimer = setTimeout(function(){
                                    $("#gb-channel-list #sw-search-input").focus().attr("tabindex","0");
                                    $(this).attr("aria-expanded","true");
                                }, 1000);
                            }
                        }
                    });
                break;
                case "320":
                	$("#sw-mystart-search").appendTo("#gb-channel-list");
                    var searchBtn = ''
                    $("#gb-channel-list").append(searchBtn);
                    $("#hp-content-two .ui-widget.app.upcomingevents .ui-articles li:gt(1)").remove();
                    var scrollBtn = '';
                    $("#hp-slideshow .rs-gallery-container .rs-gallery-desc").prepend(scrollBtn);
                    $("#gb-channel-list #gb-search-btn").on("click keydown", function(e) {
                        searchHoverTimer = "";
                        if(template.AllyClick(e)) {
                            e.preventDefault();
                            if($("#sw-mystart-search").hasClass("open")){
                            clearTimeout(searchHoverTimer);
                                $("#sw-mystart-search").removeClass("open").attr("aria-hidden","true");
                                    $(this).attr("aria-expanded","false").focus();
                            } else {
                                $("#sw-mystart-search").addClass("open").attr("aria-hidden","false");
                                searchHoverTimer = setTimeout(function(){
                                    $("#gb-channel-list #sw-search-input").focus().attr("tabindex","0");
                                    $(this).attr("aria-expanded","true");
                                }, 1000);
                            }
                        }
                    });
                break;
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

          // 
        "BuildStylesheet": function() {

            var gdBgColor80 = "rgba(" + this.HexToRgb('#2A2A86') + ", .8)";

            var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var dynStyles =	'#hp-slideshow .rs-gallery-container .rs-gallery-desc {' +
                                '	background: ' + gdBgColor80 + ';' +
                                '}' + 
                				'#hp-slideshow .rs-gallery-container .rs-gallery-desc:before {' +
                                '	background: ' + gdBgColor80 + ';' +
                                '}';

                var rules = document.createTextNode(dynStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = dynStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },
        "HexToRgb": function(hex){
            var r = parseInt((this.CutHex(hex)).substring(0,2),16);
            var g = parseInt((this.CutHex(hex)).substring(2,4),16);
            var b = parseInt((this.CutHex(hex)).substring(4,6),16);
            return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
            return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


