        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/WI02216059/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('');
            var siteNameTwo = jQuery.trim('');
            var tagline = jQuery.trim('Your Educational Community');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Wauwatosa School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend('
' + siteNameBegin + '' + siteNameEnd + '');
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
            	$("#gb-sitename").prepend('
' + siteNameOne + '');
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend('
' + siteNameTwo + '');
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend('
' + siteNameOne + '' + siteNameTwo + '');
            }
            if(tagline != ""){
            	$("#gb-sitename").append('
' + tagline + '');
            }

            var headerStructure = "";
            var headerLinks = [
            	["parents","false","#","_blank"],
                ["students","true","https://launchpad.classlink.com/wauwatosa","_blank"],
                ["staff","true","https://launchpad.classlink.com/wauwatosa","_blank"],
                ["community","false","#","_blank"]
            ]

            headerStructure += '';

            for(var h = 0; h < 4; h++) {
                if(headerLinks[h][1] == "true"){
                	headerStructure += '
' + headerLinks[h][0] + '';
                }
            }
            headerStructure += "";
            $("#gb-header-links").append(headerStructure);

        },
