         "StickyChannelBar": function() {
            var channelHeight = $("#gb-channel-list-outer").height();

            if ($(window).scrollTop() <= channelHeight) {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
            } else {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", channelHeight + "px");
                    }
                }
            }
        },
        "MegaMenu": function() {
        	//BUILD AN ARRAY OF ELEMENTS
            var channelElements = [
                [//CHANNEL ONE
                    ['About WSD',9],
                    ['School Board',5],
                    ['Departments',7],
                    ['',],
                    ['',]
                ],
                [//CHANNEL TWO
                    ['Why Wauwatosa?',5],
                    ['Prospective Staff',3],
                    ['',],
                    ['',],
                    ['',]
                ],
                [//CHANNEL THREE
                    ['Communication & Technology',10],
                    ['Forms & Fees',7],
                    ['Programs & Services',10],
                    ['Parent Partners',3],
                    ['',]
                ],
                [//CHANNEL FOUR
                    ['Elementary Schools',10],
                    ['K-8',1],
                    ['Middle Schools',2],
                    ['High Schools',2],
                    ['Special Programs',5]
                ],
                [//CHANNEL FIVE
                    ['Communication',5],
                    ['Connect',5],
                    ['Events',4],
                    ['Partners',5],
                    ['',]
                ],
                [//CHANNEL SIX
                    ['',],
                    ['',],
                    ['',],
                    ['',],
                    ['',]
                ],
                [//CHANNEL SEVEN
                    ['',],
                    ['',],
                    ['',],
                    ['',],
                    ['',]
                ],
                [//CHANNEL EIGHT
                    ['',],
                    ['',],
                    ['',],
                    ['',],
                    ['',]
                ],
            ];

            //LOOP THROUGH CHANNELS THAT HAVE DROPDOWNS
            $("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){

            	// LOWERCASE THE CHANNEL NAME
                var channelText = $("> a span", this).text().toLowerCase();

                // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                if(channelText == "home") {
                    $(this).addClass("home");
                } else if(channelText == "calendar") {
                    $(this).addClass("calendar");
                }

               	//IGNORE HOME, CALENDAR and StAFF CHANNELS
                if(channelText != "home" && channelText != "calendar") {

                    // SET THE CHANNELS INDEX
                    var channelNumber = ($("#sw-channel-list-container li.sw-channel-item.home").length) ? $(this).index() - 1 : $(this).index();

                    //CHECK TO SEE HOW MANY COLUMNS THIS CHANNEL WILL HAVE IN THE DROPDOWN
                    //AND HOW MANY SECTIONS WILL BE IN EACH COLUMN
                    var numColumns = 0;

                    //LOOP THROUGH THE COLUMN ELEMENTS IN THE CHANNEL ARRAY
                    for (i = 0; i <= 4; i++) { 
                        var columnTitle = channelElements[channelNumber][i][0].trim();
                        var numSections = channelElements[channelNumber][i][1];

                        if(columnTitle == ''){
                            //DO NOTHING

                        } else {
                            //ADD ONE TO THE numColumns VARIABLE EACH TIME THERE IS TEXT FOUND IN THE COLUMN TITLE
                            numColumns++;
                        }
                    }

                    if(numColumns > 1){
                    	// ADD MEGA CHANNEL CLASS
                    	$(this).addClass('mega-channel');

                        //ADD CLASS TO THE DROPDOWN FOR HOW MANY COLUMNS
                        $(".sw-channel-dropdown",this).addClass("column-"+numColumns);

                        //LOOP THROUGH EACH COLUMN AND BUILD THE STRUCTURE FOR IT
                        var dropDownHTML = '';
                        for(i = 0; i < numColumns; i++){
                            var numSections = channelElements[channelNumber][i][1];
                            var sectionCode = '';


                            $(".sw-channel-dropdown li:not(.tagged)", this).each(function(){
                                if($(this).index() < numSections){  
                                    sectionCode += "
"+$(this).html()+"";
                                    $(this).addClass("tagged");
                                }
                            })

                            //REMOVE SECTIONS THAT WERE ADDED TO NEW STRUCTURE SO WE CAN
                            //BEGIN OUR INDEX AT 0 AGAIN THE NEXT TIME THROUGH THE LOOP
                            $(".sw-channel-dropdown li.tagged").remove();

                            dropDownHTML += "
"+
                                                ""+
                                                "
"+channelElements[channelNumber][i][0].trim()+""+
                                                    sectionCode+
                                                ""+
                                            "";
                        }

                        //ADD DROPDOWN CODE INTO PAGE
                        $(".sw-channel-dropdown",this).html(dropDownHTML);
                        $(".sw-channel-dropdown",this).wrapInner('
');
                    }


				}

            });

            var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

            $(".column-item a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":nth-child(2)")) {
                            // IS FIRST ITEM IN FIRST COLUMN
                            if($(this).parent().parent().parent().is(":first-child")){
                                // FOCUS LAST ITEM IN LAST COLUMN
                                $("li.sw-channel-item.hover").find(".dropdown-column:nth-last-child(1) .column-item:last-child > a").focus();
                            } else {
                                // FOCUS LAST ITEM IN PREVIOUS COLUMN
                                $(this).parent().parent().parent().prev().find(".column-item:last-child > a").focus();
                            }

                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            //IS LAST ITEM IN LAST COLUMN
                            if($(this).parent().parent().parent().is(":nth-last-child(1)")){
                                // FOCUS FIRST ITEM IN FIRST COLUMN
                                $("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                            } else {
                                // FOCUS FIRST ITEM IN NEXT COLUMN
                                $(this).parent().parent().parent().next().find(".column-item:eq(1) > a").focus();
                            }
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            if($(this).parent().parent().parent().parent().parent().is(":first-child")) {
                                // FOCUS DROPDOWN BUTTON
                                $("#sw-search-button").focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(".sw-channel-item.hover").prev().find("> a").focus();
                            }
                        } else {
                            e.preventDefault();

                            $(".sw-channel-item.hover").next().find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM IN FIRST COLUMN
                        $("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM IN LAST COLUMN
                        $("li.sw-channel-item.hover").find(".dropdown-column:nth-last-child(2) .column-item:last-child > a").focus();
                    break;

                    case csKeyCodes.esc:
                        $(".sw-channel-item.hover").find("> a").focus();
                    break;
                }
            });
        },
        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

           //RENAME FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            //REPLACE MORE-LINK TEXT
            $("a.more-link span").text("View All Articles");

            //AUTO-FOCUS UERSNAME SIGIN FIELD
            $("#swsignin-txt-username").focus();

            //CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
                if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                    $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                    //ADD SIGNED OUT BREADCRUMB
                    $(".ui-breadcrumbs").append("
Signed Out");
                }

            //WRAP HEADLINES
            $(".ui-widget.app.headlines").not(".hp.column.one .ui-widget.app.headlines").each(function(){
                $(".ui-article", this).each(function(){
                    $(".ui-article-title, .ui-article-description, .ui-article-controls",this).wrapAll('
');
                    $(".clear",this).remove();
                });
            });

            //ADD READ FULL STORY BUTTON TO EACH HEADLINE
            $(".hp.column.one .ui-widget.app.headlines").each(function(){
                $(".ui-article", this).each(function(){
                    var headlineURL = $("h1 a", this).attr("href");
                    $('read more').insertAfter($(".ui-article-description", this));
                });
            });

            //ADD SIGN IN BREADCRUMB
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

                //REGISTER BREADCRUMB
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            //SEARCH RESULTS BREADCRUMB
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            //CHANGE TEXT OF CALENDAR LINK
            $("a.view-calendar-link").text("View Full Calendar");

            //LIMIT EVENTS ON HOMEPAGE - REMOVE IF NOT NEEDED
            $("#hp-content-one .ui-widget.app.headlines .ui-articles li:gt(4)").remove();


            //SCROLL FUNCTION FOR BACK TO TOP BUTTON
            $("#gb-scroll-up").click(function(e){
                    e.preventDefault();
                    $("html, body").animate({ scrollTop: $("body").offset().top}, 1000);
                    $("li.sw-channel-item:first-child a").focus();
            });

            $("#gb-scroll-up").keydown(function(e){
                if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                    e.preventDefault();
                    $("html, body").animate({ scrollTop: $("body").offset().top}, 1000);
                    $("li.sw-channel-item:first-child a").focus();
                }
            });

            //SLIDESHOW ARROW SCROLL
            $("#hp-scroll-down").click(function(){
                $("html, body").animate({scrollTop: ($("#gb-global-icons-outer").offset().top)}, 1000); 
            });

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            //APPLY CLASS TO COLUMN FIVE FIRST APP IF IT IS THE ONLY APP
            var appNumber = $("#sw-content-container5 > div").length;
            if(appNumber < 2){
            	$("#sw-content-container5 > div:first-child").addClass("only-app");
            }


            //Customize Apps
            this.UpcomingEvents();
        },
        "Footer": function() {
        	//ADD LOGO
            var footerLogoSrc = jQuery.trim("/cms/lib/WI02216059/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
                var footerSrcSplit = footerLogoSrc.split("/");
                var footerSrcSplitLen = footerSrcSplit.length;
                if((footerLogoSrc != "") && (footerSrcSplit[footerSrcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-footer-logo").append('');
                } else {
                    $("#gb-footer-logo").append('');
                }
            //PHONE AND FAX NUMBERS
            if("414-773-1000" != "") {
                $(".gb-footer-contact.phone").removeClass("hidden");
            }
            if("" != "") {
                $(".gb-footer-contact.fax").removeClass("hidden");
            }

            $("#gb-social-icons .icn").hover(function(){
            	$('span[class^="icon-font"]',this).animate({
                	bottom: "10px"
                }, 300, function() {

                });

            }, function(){
            	$('span[class^="icon-font"]',this).animate({
                	bottom: "0px"
                }, 300, function() {

                });
            });
            $("#gb-social-icons .icn").hover(function(){
            	$('img',this).animate({
                	bottom: "10px"
                }, 300, function() {

                });

            }, function(){
            	$('img',this).animate({
                	bottom: "0px"
                }, 300, function() {

                });
            });

            $("#gb-social-icons .icn").focusin(function(){
            	$('span[class^="icon-font"]',this).animate({
                	bottom: "10px"
                }, 300, function() {

                });

            });

            $("#gb-social-icons .icn").focusout(function(){
            	$('span[class^="icon-font"]',this).animate({
                	bottom: "0px"
                }, 300, function() {

                });
            });

            $("#gb-social-icons .icn").focusin(function(){
            	$('img',this).animate({
                	bottom: "10px"
                }, 300, function() {

                });

            });

            $("#gb-social-icons .icn").focusout(function(){
            	$('img',this).animate({
                	bottom: "0px"
                }, 300, function() {

                });
            });

            var footerEmail = '';
            if(footerEmail !=""){
            	$(".gb-footer-contact.email").append('');
            }

            //ADD CLASS TO SITEMAP TO MAKE IT ACCESSIBLE BUT HIDDEN
            $("#gb-sitemap a span").addClass("cs-accessible-hidden");

            //GET SCHOOLWIRES FOOTER LINKS
            var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
            var feedbackText = $("div#sw-footer-links li:eq(0) a").text();
            var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
            var termsText = $("div#sw-footer-links li:eq(2) a").text();
            var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
            var policyText = $("div#sw-footer-links li:eq(1) a").text();
            var swLogo = $("#sw-footer-logo a img").attr("src");
            var swURL = $("#sw-footer-logo a").attr("href");

            $("a#feedback").attr("href", feedbackURL).text(feedbackText);
            $("a#terms").attr("href", termsURL).text(termsText);
            $("a#policy").attr("href", policyURL).text(policyText);  
            var swLogoSrc = '';
            $("#sw-logo").append(swLogoSrc);
        },
        "Search": function() {
            //CUSTOM MYSTART SEARCH PLACEHOLDER
            //SEARCH TEXT
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
            $("div#sw-mystart-search input#sw-search-input").attr("value", "search");
            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "search") {
                    $(this).val("");
                }
                $("#sw-mystart-search").addClass("focus");
            });
            $("div#sw-mystart-search input#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("search");
                }
                $("#sw-mystart-search").removeClass("focus");
            });
        },


