                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                    var dayText = $(".upcoming-column.left .joel-day",this).html();
                    $(".upcoming-column.right",this).prepend('' + dayText + '');
                    $(".upcoming-column.left .sw-calendar-block-date",this).append('' + dayText + '');
                    $(".upcoming-column.left .joel-day",this).remove();

                    $(".sw-calendar-block-date", this).wrapInner('');
                        $(".joel-month", this).parents(".sw-calendar-block-date").prepend($(".joel-month", this));
                });
                $(".view-calendar-link").wrap('
');
            }
