        "CheckTranslateCookie": function() {
        	var translateCookie = this.ReadCookie("googtrans");

        	if(translateCookie) {
            	// CHECK PROPERTIES SO THAT WE'RE A LITTLE LESS OBTRUSIVE
                if(!this.TranslateCookieExists || translateCookie != this.TranslateCookieValue) {
                	// SET TRANSLATE PDF LINKS
                    this.SetTranslatePdfLinks();

                    // UPDATE PROPERTIE
                    this.TranslateCookieExists = true;
                    this.TranslateCookieValue = translateCookie;
                }
            } else {
            	// CHECK PROPERTY SO THAT WE'RE A LITTLE LESS OBTRUSIVE
            	if(this.TranslateCookieExists) {
            		// RESET PDF LINKS
                    this.ResetPdfLinks();

                    // UPDATE PROPERTY
                    this.TranslateCookieExists = false;
                }
            }
        },

        "SetTranslatePdfLinks": function() {
        	var _this = this;

        	// ALL LINKS THAT CONTAIN ".pdf"
        	$("a[href*='.pdf']").each(function() {
            	var pdfLink = '';

            	if($(this).is("[data-original-href]")) {
                	pdfLink = encodeURIComponent($(this).attr("data-original-href"));
                } else {
                	pdfLink = encodeURIComponent($(this).attr("href"));

                    // SET ORIGINAL VALUE FOR LATER USE
                	$(this).attr("data-original-href", $(this).attr("href"));
                }
            	// GET BRANDED COOKIE VALUE (EX: "/en/es"), STRIP OFF THE LEADING "/", AND REPLACE THE SECOND "/" WITH "|" SO THAT IT'S PROPERLY FORMATTED FOR translate.google.com
            	var langPair = _this.ReadCookie("googtrans").substring(1).replace("/", "|");

                // REPLACE THE PDF LINK
                $(this).attr("href", "//translate.google.com/translate?u=" + pdfLink + "&langpair=" + langPair + "&hl=en&anno=0&ie=UTF8");
            });
        },

        "ResetPdfLinks": function() {
        	var _this = this;

        	$("a[data-original-href]").each(function() {
                $(this).attr("href", $(this).attr("data-original-href"));
                $(this).removeAttr("data-original-href");
            });
        },

        "CreateCookie": function (name, value, days) {
              var expires;

              if (days) {
                  var date = new Date();
                  date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                  expires = "; expires=" + date.toGMTString();
              } else {
                  expires = "";
              }
              document.cookie = escape(name) + "=" + escape(value) + expires + "; path=/";
          },

          "ReadCookie": function(name) {
              var nameEQ = escape(name) + "=";
              var ca = document.cookie.split(';');
              for (var i = 0; i < ca.length; i++) {
                  var c = ca[i];
                  while (c.charAt(0) === ' ') c = c.substring(1, c.length);
                  if (c.indexOf(nameEQ) === 0) return unescape(c.substring(nameEQ.length, c.length));
              }
              return null;
          },

          "EraseCookie": function(name) {
              this.CreateCookie(name, "", -1);
          }
    };

