	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":
				checkGradient();
			break;
			case "640":
				checkGradient();
			break;
			case "480":

			break;
			case "320":

			break;
		}
	}

    function channelCheck(){ //Adam - 10.26.17 - 02962916
    	var channelNum = $(".sw-channel-item").length;

        if(channelNum >= 6){
        	$("#gb-page").addClass("many-channels");
        }
    }

	function checkFindItFast(){ //Added by Adam - 10.3.17 - Case #02929271
    	$(".ac-dropdown-container.find-it-fast").each(function(){
        	$(this).find(".ac-dropdown-list li").each(function(){
            	var linkHref = $("a", this).attr("href");
                if(linkHref == "#"){
                	$(this).remove();
                }
            })
        })
    }

