/* ------------------------------   NPS NETWORK CHECK  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	//NPS BLOCKS FB AND TW INTERNALLY SO WE NEED TO SHOW SUBSTITUTE CONTENT FOR THOSE REGIONS WHEN IN THE NETWORK
    function npsNetworkCheck(){

        //ADD A DEFAULT CLASS TO THE FB AND TW REGIONS THAT WILL ALLOW DISPLAYING OF SUBSTITUTE CONTENT
        $(".hp #sw-content-container4, .hp #sw-content-container5").addClass("no-iframe");


        //LISTEN FOR CHANGES IN REGIONS WHERE FB AND TW EMBEDS ARE
        $('.hp #sw-content-container4').bind('DOMNodeInserted ', function(event) {
        	if(!$(this).hasClass("loading")){
            	//IF WE GET THIS FAR, THE IFRAMES ARE BEING INSERTED AND WE CAN SHOW THE EMBED APPS AND HIDE THE SUBSTITUTE APPS
            	$(this).addClass('loading').removeClass('no-iframe');
            }

    	});

        //LISTEN FOR CHANGES IN REGIONS WHERE FB AND TW EMBEDS ARE
        $('.hp #sw-content-container5').bind('DOMNodeInserted ', function(event) {
        	if(!$(this).hasClass("loading")){
            	//IF WE GET THIS FAR, THE IFRAMES ARE BEING INSERTED AND WE CAN SHOW THE EMBED APPS AND HIDE THE SUBSTITUTE APPS
            	$(this).addClass('loading').removeClass('no-iframe');
            }

    	});

    }

