/* ------------------------------   STICKY HEADER  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function stickyHeader(){
		$(document).scroll(function() {
			var distanceFromTop = $(this).scrollTop();

			if(!$(".lt-1024").length){
				if(distanceFromTop >= 55){
					$("#gb-page.hp").addClass("logo-gone");
				} else {
					$("#gb-page.hp").removeClass("logo-gone");
				}
			}

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", $("#gb-header").outerHeight(true) + $("#gb-mystart-outer").outerHeight(true));
                }
            }

			$("#sw-maincontent").css({
                "display": "block",
                "position": "relative",
                "top": "-" + $("#gb-header").outerHeight(true) + $("#gb-mystart-outer").outerHeight(true) + "px"
            });

			plx();
		});
	}

	function plx(){
		var scrolled = $(window).scrollTop();
		$('#plx-bg').css('top',-(scrolled*0.2)+'px');
	}

	function scrollToContent(){
		$("#scroll-down").click(function(){
    		$('html, body').animate({
		        scrollTop: $(this).offset().top + 70
		    }, 500);
    	});
	}

