	function loadIcons(){
		head.ready("globalIcons", function(){
			$(".media-area-icons").creativeIcons({
				"iconNum"       : "4", // NUMBER OF ICONS
				"icons"         : [
				["Student Program/12.png", "Transportation", "/transportation", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
				["Student Program/15.png", "School Meals", "http://nps.nutrislice.com/", "_blank"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
				["People/4.png", "School Board Information", "/schoolboard", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
