    function extraIcons(){

    	var showMilitary = "true";
        var showCareers = "true";
        var iconContent = '';

        if(showMilitary == "true"){
        	iconContent += 'Military Families';
        }
        if(showCareers == "true"){
        	iconContent += 'Careers';
        }

		$(iconContent).appendTo(".media-area-icons");

    }

    function noIcons(){
    	var showMilitary = "true";
        var showCareers = "true";


    	var numIcons = "4";
        if (numIcons == 0 && showMilitary == 'false' && showCareers == 'false'){
        	$("#gb-page").addClass("no-bar-icons");
        }
    }
