	function padSiteName(){
    	var showLogo = "true";

        var logoWidth = $("#gb-logo-wrapper .gb-logo img").width();

        if(showLogo != "true"){
        	$(".hp #gb-sitename").css({
                "visibility": "visible"
            });
        } else {



            $(".hp #gb-sitename").css({
                "padding-left": logoWidth+18,
                "visibility": "visible"
            });
            $("#gb-logo-wrapper .gb-logo").css({
                "visibility": "visible"
            });

        }

	}
