	function dynamicContent() {

		// ********** MYSTART ********** //
		$(document).off("click", ".sw-mystart-dropdown");
		$(document).on("click", ".sw-mystart-dropdown, .ac-dropdown-container", function(e) {
			$(".sw-dropdown, .ac-dropdown", this).attr("aria-hidden","false").slideDown(300, "swing", function(){

				setTimeout(function(){
					$(".sw-dropdown, .ac-dropdown", this).focus();
				}, 100);
			});
			$(this).addClass("open");

		});

		$(".sw-mystart-dropdown, .ac-dropdown-container").hover(function(e) {
			e.preventDefault();
		}, function() {

			$(".sw-dropdown, .ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
			$(this).removeClass("open"); 
		});

        if($("#sw-mystart-account").length){
        	$(".ac-dropdown-container.my-options").show();
            $("#sw-mystart-mypasskey").prependTo("#gb-mystart-right");
        } else {
        	 //HIDE REGISTER BUTTON
              if(!$("#ui-btn-register").length){
                  $(".gb-mystart-button.signin").show();
              } else {
              	$(".gb-mystart-button.signin, .gb-mystart-button.register").show();
              }

        }

		// ADD SITENAME
		var siteTagline = jQuery.trim('The cornerstone of a proudly diverse community');

        var siteName = "Norfolk Public Schools";
		$("#gb-sitename").prepend("
" + siteName + "
"+siteTagline+"");

		//SEARCH
		$("#gb-search-control").click(function(){
			var clickedObj = $(this);
			if($(clickedObj).hasClass("open")){
				$("#sw-mystart-search").animate({ 
					width: 0, 
					marginRight: 0
				}, "slow", function() {

	    			setTimeout(function(){
						$("#sw-mystart-search").hide().attr("aria-hidden", "true").removeClass("open");
					}, 250);

	    		});
	    		$(clickedObj).removeClass("open");


			} else {
				$("#sw-mystart-search").show().attr("aria-hidden", "false").addClass("open");
				$("#sw-mystart-search").animate({ 
					width: 135,
					marginRight:7 
				}, "slow", function() {

	    			setTimeout(function(){
						$("#sw-search-input").focus();
					}, 100);
					$(clickedObj).addClass("open");
	    		});
			}

		});
        $("div#sw-mystart-search.sw-mystart-nav").unbind();

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){

			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});



		$(".sw-channel-dropdown").each(function(){
			$(this).wrapInner("
");
		});

		$(".sw-channel-item:last").addClass("last");

        //MOVE CALENDAR CHANNEL AFTER HOME
        var showCalendar = "No";

        if(showCalendar == 'Yes'){
        	$(".sw-channel-item:last").addClass("calendar");
        }

        $(".sw-channel-item:first").after($(".sw-channel-item.calendar"));

        $("nav").css("visibility", "visible");

        var findItFastToggle = "true";

        if(findItFastToggle == "false"){
        	$(".find-it-fast").remove();
        } else {
            $(".find-it-fast").css("visibility","visible");
        }

		// ********** BODY ********** //

        //SHOW TOP SOCIAL ICONS
        var showSocial = "true";

        if(showSocial == "true"){
        	$("#gb-top-icons").show();
        }

        // WRAP CALENDAR LINK FOR STYLING 
        //$(".hp .view-calendar-link").wrap("
");

		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		//PLACE THE HEADLINES READ MORE BUTTON THE THE APP FOOTER
		$(".headlines").each(function(){
			$("a.more-link span", this).text("View All").parent().css("display","inline-block");
		});

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//ADD SIGNED OUT BREADCRUMB
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        	//ADD SIGN IN BREADCRUMB
        	if($("#swlogin").length){
        		$("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
        		$(".ui-breadcrumbs").append("
Sign In");
                $("#swlogin .ui-description").html("Please login with your Norfolk Public Schools network account and password using the 'NPS Login' button below.
WARNING: Unauthorized access to this system is forbidden and will be prosecuted by law. By accessing this system, you agree that your actions may be monitored if unauthorized usage is suspected.
You can use this site without being registered or signing in, but registered users who sign in may have access to additional features and information. Please remember that your password is case-sensitive.");
        	}

        	//REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}


	        // ADD READ FULL STORY BUTTON TO EACH HEADLINE
	        $(".ui-widget.app.headlines").each(function(){
	            $(".ui-article", this).each(function(){
	                var headlineURL = $("h1 a", this).attr("href");
	                $(".ui-article-description", this).after('Read More');
	            });
	        });

	        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
	        $("div.ui-widget.app .ui-widget-detail img")
	            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
	            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
	            .each(function() {
	                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
	                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
	                }
	        });

	        // MAKE NAVIGATION APPS ACCESSIBLE
	        head.ready("accessibleNavApp", function(){
	            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
	        });

	        //WRAP OL CONTENT FOR COLORED NUMBERS
	        $("ol li").wrapInner('');


		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));	

		// FOOTER LINKS
		$(".to-top").click(function(e){
			e.preventDefault();
            		$("html, body").animate({ scrollTop: 0 }, "slow", function() {
            			$("li.sw-channel-item:first-child > a").focus();
            		});
		});
	}  
