        var filePath = "https://www.npsk12.com//cms/lib/VA02208074/Centricity/Domain/4555/NPS Coronavirus Update Newsletter_4_26_20.pdf";
        
        if (filePath !== ''){
        	$("#doc-viewer-text-21238").removeClass("ui-helper-hidden");
        }
		
        filePath = encodeURIComponent(filePath);
       
        var url = "https://docs.google.com/viewer?url=" + filePath + "&embedded=true";
        
        $("#doc-viewer-21238").attr("src", url);
	});

